function fnDCheck_Click(obj)
{
	if(global.gv_WorkFlag == global.gv_INSERT)
	{
		if (fnEmpty(RECV_NUM.Text))
		{
			alert('접수번호를 입력하세요.');
			return;
		}
		
		dsCheckKey.DeleteAll();
		
		var sSIDO_CODE = dsPT_R_BASIC_STATEMENT.GetColumn(dsPT_R_BASIC_STATEMENT.row, "SIDO_CODE");
	    var sTMP_WRT_NUM = dsPT_R_BASIC_STATEMENT.GetColumn(dsPT_R_BASIC_STATEMENT.row, "TMP_WRT_NUM");

		dsPT_R_MOT_STE.AddRow();
		//dsPT_R_MOT_STE.SetColumn(0, "CHGBRE_SEQ","1");
		dsPT_R_MOT_STE.SetColumn(0, "RECV_NUM",RECV_NUM.Text);
		dsPT_R_MOT_STE.SetColumn(0, "MOT_PROC","S");
		dsPT_R_MOT_STE.SetColumn(0, "SIDO_CODE",sSIDO_CODE);
		dsPT_R_MOT_STE.SetColumn(0, "TMP_WRT_NUM",sTMP_WRT_NUM);
		
		var sArgs = "workMODE=CHECK RECV_NUM=" + quote(RECV_NUM.Text) + " scSIDO_CODE=" + global.g_Sd_cd;;
		var sTid = "CHECK_KEY";
		var sUrl = "ICCS::cm/CmAction.do?cmd=StdReceiptMgrCN";
		var sInDs = "";
		var sOutDs = "dsCheckKey=output dsCheckKey2=output2 dsCheckKey3=output3 dsCheckKey4=output4";
		var sCallBack = "fn_CallBack";
			
		Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
	}
}


