function tbMain_tbInfo_TUP_AOM_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam)
{
		if(nChar == 13)
  {
     
     var sTUP_AOM=tbMain.tbInfo.TUP_AOM.Value;
     var scTUP_AOM=ToNumber(sTUP_AOM);
     
     //좌수
     a=323.685;
     
           //alert(scTUP_AOM/a);
           var jasu=scTUP_AOM/a;
           //alert("좌수 "+jasu);

          //tbMain.tbInfo.MaskEdit0.Text=round(scTUP_AOM/a,0);
          dsPT_R_BASIC_STATEMENT.SetColumn(0, "UNION_INVEST_NUM", round(scTUP_AOM/a,0));

   }
}
