//////////////////////////////////////////////////////////////////
// Key Down 관련 이벤트
//////////////////////////////////////////////////////////////////
function fnKey_Down(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam)
{
	if(nChar == "81" && bControl == true) fnTablesBinding();
	else if(nChar == "49" && bControl == true) fnInsert();
	else if(nChar == "50" && bControl == true) fnModify();
	else if(nChar == "51" && bControl == true) fnRemove();
	else if(nChar == "83" && bControl == true) fnSave();
	else if(nChar == "80" && bControl == true) fnReport();
	else if(nChar == "13") 
	{
		var sCompnentname = GetCurrentComponent(true);
		if(sCompnentname.GetType() == "TextArea") return;
		GetNextComponent(true).SetFocus();
	}
	else if(nChar == "120") 
	{
		var nTabIndex = tbMain.TabIndex;
		
		if(nTabIndex == tbMain.TabCount - 1)
			tbMain.TabIndex = 0;
		else
			tbMain.TabIndex = nTabIndex + 1;
	}
}

//////////////////////////////////////////////////////////////////
// Validation check 이벤트
//////////////////////////////////////////////////////////////////
/**
 1. 함수명 : fnKillFocus
 2. 기  능 : Control Leave 이벤트 핸들러
 3. Params 
    없음
 4. Return
    없음
*/
function fnKillFocus(obj)
{
   if (obj == tbMain.tbEtc.NM_KOR)
	{
		if( fnEmpty(tbMain.tbEtc.NAME_HANJA.Text) )
			tbMain.tbEtc.NAME_HANJA.Text = tbMain.tbEtc.NM_KOR.Text;
	}
	
	if (obj == tbMain.tbEtc.GUBUN)
	{
		if (dsPT_R_ASSI_TRANS_STATEMENT.GetColumn(0, "COMMANA_CLASS") == 2 && 
		dsPT_R_REFFER.GetColumn(dsPT_R_REFFER.row, "POS_CLASS") != "P4")
		{
			alert('개인사업자의 경우 대표자만을 선택할 수 있습니다.');
			dsPT_R_REFFER.SetColumn(dsPT_R_REFFER.row, "POS_CLASS", "P4");
		}

		dsPT_R_REFFER.SetColumn(dsPT_R_REFFER.row, "POS_NAME", tbMain.tbEtc.GUBUN.Text);
		
		if (dsPT_R_REFFER.GetColumn(dsPT_R_REFFER.row, "POS_CLASS") == "P4" ||
			dsPT_R_REFFER.GetColumn(dsPT_R_REFFER.row, "POS_CLASS") == "P1")
		{
			var sMasterNm = dsPT_R_BASIC_CHANGE_HISTORY.GetColumn(0, "REP_NM_KOR");
			var sDetailNm = dsPT_R_REFFER.GetColumn(dsPT_R_REFFER.row, "NM_KOR");
			
			if (sMasterNm == sDetailNm)
			{
				dsPT_R_ASSI_TRANS_STATEMENT.SetColumn(0, "REP_SSN1", 
											dsPT_R_REFFER.GetColumn(dsPT_R_REFFER.row, "SSN1"));
				dsPT_R_ASSI_TRANS_STATEMENT.SetColumn(0, "REP_SSN2", 
											dsPT_R_REFFER.GetColumn(dsPT_R_REFFER.row, "SSN2"));
			}
		}
	}
}

/**
 1. 함수명 : fnTablesBinding
 2. 기  능 : 검색조건에 대한 검색
 3. Params 
    없음
 4. Return
    없음
*/
function fnTablesBinding()
{
	if(_sKEY1=='_sKEY2=')
	{
     var _sKEY1 = dsPT_R_ASSI_TRANS_STATEMENT.GetColumn(0, "ASSI_TMP_WRT_NUM");
     var _sKEY2 = dsPT_R_ASSI_TRANS_STATEMENT.GetColumn(0, "RECV_NUM");

	var sArgs = "workMODE=TransView ";
	sArgs += " scTMP_WRT_NUM=" + quote(_sKEY1) + " scRECV_NUM=" + quote(_sKEY2)+" scSIDO_CODE=" + global.g_Sd_cd;
	
	var sTid = "TransView";
	var sUrl = "ICCS::cm/CmAction.do?cmd=TransMgrCN";
	var sInDs = "";
	
    var sOutDs = "dsPT_R_COMPANY_MASTER=output dsPT_R_BASIC_CHANGE_HISTORY=output2 " ;
	sOutDs += "dsPT_R_REFFER=output3 dsPT_R_ENGINEER_CHANGE_TEMP=output4 " ;
	sOutDs += "dsPT_R_REFUSAL_REASON=output5 dsPT_R_SUBSIDIARY=output6 ";
	sOutDs += "dsPT_R_COM_DEFICIT=output7 dsPT_R_ASSI_TRANS_STATEMENT=output8 ";		
	sOutDs += "dsPT_R_MOT_STE=output9 dsPT_R_TRANS_COMPANY_BEFORE=output10 ";
	sOutDs += "dsPT_R_DEFICIT=output11 dsPT_R_DEFICIT_CODE=output12 ";
	var sCallBack = "fn_CallBack";
		
	Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
	break;
    }
    
	// 양도양수, 법인전환, 분할합병 상관없이 다 가져와서 담당자가 직접 삭제하는것.
	// 협회와 부산담당자의 말이 다르다. -_-
	
	var comMODE = "STD002";
	
	if (this.MOT_STE.Value == "STD003"){	
		comMODE = "STD003";
	}
	
	var sArgs = "workMODE=TransView ";
	sArgs += " scTMP_WRT_NUM=" + quote(_sKEY1) + " scRECV_NUM=" + quote(_sKEY2)+" scSIDO_CODE=" + global.g_Sd_cd;
	
	var sTid = "TransView";
	var sUrl = "ICCS::cm/CmAction.do?cmd=TransMgrCN";
	var sInDs = "";
	
    var sOutDs = "dsPT_R_COMPANY_MASTER=output dsPT_R_BASIC_CHANGE_HISTORY=output2 " ;
	sOutDs += "dsPT_R_REFFER=output3 dsPT_R_ENGINEER_CHANGE_TEMP=output4 " ;
	sOutDs += "dsPT_R_REFUSAL_REASON=output5 dsPT_R_SUBSIDIARY=output6 ";
	sOutDs += "dsPT_R_COM_DEFICIT=output7 dsPT_R_ASSI_TRANS_STATEMENT=output8 ";		
	sOutDs += "dsPT_R_MOT_STE=output9 dsPT_R_TRANS_COMPANY_BEFORE=output10 ";
	sOutDs += "dsPT_R_DEFICIT=output11 dsPT_R_DEFICIT_CODE=output12 ";
	

	var sCallBack = "fn_CallBack";
	//http.timeout = 20;
	
	Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
}




function fn_CallBack(Svcid, sErrCode, sErrMsg) 
{
	http.sync = false;
	
	if (sErrCode < 0) 	
	{
		if (svcid == "TransView")
		{
			alert('데이터 조회 중 오류가 발생하였습니다.');
			return;
		}
		if (svcid == "SAVE")
		{
			alert('데이터 저장 중 오류가 발생하였습니다.');
			return;
		}
			if (svcid == "GET_CORP")
		{
			alert('양도자 조회 중 오류가 발생하였습니다.');
			return;
		}
			if (svcid == "GET_CORP2")
		{
			alert('양수자 조회 중 오류가 발생하였습니다.');
			return;
		}
			if (svcid == "WORK_RESULTS")
		{
			alert('실적 처리 중 오류가 발생하였습니다.');
			return;
		}
			if (svcid == "KICAIfSendCUD")
		{
			alert('협회 데이터 전송 중 오류가 발생하였습니다.');
			return;
		}
			if (svcid == "EngListIns")
		{
			alert('기술자 정보가 없습니다. 주민번호를 확인하시기 바랍니다.');
			tbMain.tb_Engineer.ENGINEER_SSN1.Value = "";
			tbMain.tb_Engineer.ENGINEER_SSN2.Value = "";
			tbMain.tb_Engineer.ENGINEER_SSN1.SetFocus();
			return;
		}
		
		
	}
	
	// 협회 송신 큐에 넣기가 성공한 경우
	if (svcid == "KICAIfSendCUD")
	{
		dsPT_R_BASIC_CHANGE_HISTORY.SetColumn(0, "SND_STE", "O");
		dsPT_R_BASIC_CHANGE_HISTORY.SetColumn(0, "SND_DT", Today());
	}

if (svcid == "EngListIns"){
		
		if (dsPT_R_ENGINEER_MASTER.rowcount =1)
	{
		dsPT_R_ENGINEER_CHANGE_TEMP.SetColumn(dsPT_R_ENGINEER_CHANGE_TEMP.row,"ENGINEER_NM",dsPT_R_ENGINEER_MASTER.GetColumn(dsPT_R_ENGINEER_MASTER.row,"NM_KOR"));
		dsPT_R_ENGINEER_CHANGE_TEMP.SetColumn(dsPT_R_ENGINEER_CHANGE_TEMP.row,"ENGINEER_SSN1",dsPT_R_ENGINEER_MASTER.GetColumn(dsPT_R_ENGINEER_MASTER.row,"ENGINEER_SSN1"));
		dsPT_R_ENGINEER_CHANGE_TEMP.SetColumn(dsPT_R_ENGINEER_CHANGE_TEMP.row,"ENGINEER_SSN2",dsPT_R_ENGINEER_MASTER.GetColumn(dsPT_R_ENGINEER_MASTER.row,"ENGINEER_SSN2"));
		dsPT_R_ENGINEER_CHANGE_TEMP.SetColumn(dsPT_R_ENGINEER_CHANGE_TEMP.row,"CARE_BOOK_ISSUE_NUM",dsPT_R_ENGINEER_MASTER.GetColumn(dsPT_R_ENGINEER_MASTER.row,"CARE_BOOK_ISSUE_NUM"));
		dsPT_R_ENGINEER_CHANGE_TEMP.SetColumn(dsPT_R_ENGINEER_CHANGE_TEMP.row,"TMP_FIELD",dsPT_R_ENGINEER_MASTER.GetColumn(dsPT_R_ENGINEER_MASTER.row,"TMP_GRADE"));
		dsPT_R_ENGINEER_CHANGE_TEMP.SetColumn(dsPT_R_ENGINEER_CHANGE_TEMP.row,"CARE_BOOK_VAL_START_DT",dsPT_R_ENGINEER_MASTER.GetColumn(dsPT_R_ENGINEER_MASTER.row,"CARE_BOOK_VAL_START_DT"));
		dsPT_R_ENGINEER_CHANGE_TEMP.SetColumn(dsPT_R_ENGINEER_CHANGE_TEMP.row,"CARE_BOOK_VAL_END_DT",dsPT_R_ENGINEER_MASTER.GetColumn(dsPT_R_ENGINEER_MASTER.row,"CARE_BOOK_VAL_END_DT"));
		//dsPT_R_ENGINEER_CHANGE_TEMP.SetColumn(dsPT_R_ENGINEER_CHANGE_TEMP.row, "TMPSSN", bRet[1] + bRet[2]);
		//dsPT_R_ENGINEER_CHANGE_TEMP.SetColumn(dsPT_R_ENGINEER_CHANGE_TEMP.row, "ENGINEER_GRADE", bRet[7]);	}
	
	//기술자 중복체크
	var rtn;
	for(i=0;i<dsPT_R_ENGINEER_CHANGE_TEMP.count;i++)
{
rtn = dsPT_R_ENGINEER_CHANGE_TEMP.SearchRow(" ENGINEER_SSN1 = '" + dsPT_R_ENGINEER_CHANGE_TEMP.GetColumn(i,"ENGINEER_SSN1")+ "' &&  ENGINEER_SSN2 = '" + dsPT_R_ENGINEER_CHANGE_TEMP.GetColumn(i,"ENGINEER_SSN2")+ "'",i+1,dsPT_R_ENGINEER_CHANGE_TEMP.count);
