			var sTid = "RECV_NUM_CHECK_SV";
			var sUrl = "ICCS::cm/CmAction.do?cmd=TransMgrCCUDN";
			var sInDs = "dsPT_R_ASSI_TRANS_STATEMENT=dsPT_R_ASSI_TRANS_STATEMENT:u ";
			sInDs += " dsPT_R_MOT_STE=dsPT_R_MOT_STE:u";
			
			var sOutDs = "";
			var sCallBack = "fn_CallBack";
			
			Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
			
		}
	}
		if (svcid == "DOC_CODE_CHECK_KEY")
	{
		if (dsCheckKey.rowcount or dsCheckKey2.rowcount or dsCheckKey3.rowcount or dsCheckKey4.rowcount or dsCheckKey5.rowcount or dsCheckKey6.rowcount > 0)
		{
			alert('존재하는 문서번호입니다.\n예)정보화담당관-1234-1(2011)');
			Edit0.Text = "";
			Edit0.SetFocus();
		}
	}
	
	if (svcid == "RECV_NUM_CHECK_SV"){
		global.gv_WorkFlag = global.gv_MODIFY;
		fnInitButtonBar();
				
		_sKEY2 = this.RECV_NUM.value;
		_sKEY1 = "";	

		//fninitRECV_DIV();	// 접수처 세팅
	}

}

