function fnInquiry()
{
	global.gv_WorkFlag = global.gv_INQUIRY;
	fnInitButtonBar();
}

/**
 1. 함수명 : fnInsert
 2. 기  능 : 신규버튼 이벤트 핸들러
 3. Params 
    없음
 4. Return
    없음
*/
function fnInsert()
{
	//입력 모드로 최초화면 로드 시 흠결코드 조회
	var sArgs = "workMODE=GETDEF ";
	var sTid = "GETDEF";
	var sUrl = "ICCS::cm/CmAction.do?cmd=TransMgrCN";
	var sInDs = "";
	var sOutDs = "dsPT_R_DEFICIT_CODE=output";
	var sCallBack = "fn_CallBack";
	
	Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
	
}

function fnCancel()
{
	if(!Confirm('취소하시겠습니까?')) return;
	
	if(global.gv_WorkFlag == global.gv_INQUIRY)
	{
		//키값을 자동으로 생성할 경우 삭제하는 로직을 추가해주세요
		Close();
	}
	else
	{
		fnRejectChange(this.dsPT_R_COMPANY_MASTER);
		fnRejectChange(this.dsPT_R_BASIC_CHANGE_HISTORY);
		alert('취소되었습니다.');
	}

	fnInquiry();
}

