										dsPT_R_ASSI_TRANS_STATEMENT.GetColumn(0, "TRAN_DETAIL_ADDR"));
		dsPT_R_COMPANY_MASTER.SetColumn(0, "WRT_ID", global.g_User_ID);
		
		dsPT_R_BASIC_CHANGE_HISTORY.SetColumn(0, "CHGBRE_SEQ",_chgbre_seq);
		dsPT_R_BASIC_CHANGE_HISTORY.SetColumn(0, "CHG_DT", registDt);
		dsPT_R_BASIC_CHANGE_HISTORY.SetColumn(0, "REGIST_YMD", 
										dsPT_R_ASSI_TRANS_STATEMENT.GetColumn(0, "ASSI_REGIST_YMD"));
											
		dsPT_R_BASIC_CHANGE_HISTORY.SetColumn(0, "NAME", 
										dsPT_R_ASSI_TRANS_STATEMENT.GetColumn(0, "TRAN_NAME"));
		dsPT_R_BASIC_CHANGE_HISTORY.SetColumn(0, "ADDR_TEL_NUM", 
										dsPT_R_ASSI_TRANS_STATEMENT.GetColumn(0, "TRAN_TELNUM"));
		dsPT_R_BASIC_CHANGE_HISTORY.SetColumn(0, "REP_NM_KOR", 
										dsPT_R_ASSI_TRANS_STATEMENT.GetColumn(0, "TRAN_REP"));
		dsPT_R_BASIC_CHANGE_HISTORY.SetColumn(0, "MANA_NUM", 
										dsPT_R_ASSI_TRANS_STATEMENT.GetColumn(0, "TRAN_MANA_NUM"));
		dsPT_R_BASIC_CHANGE_HISTORY.SetColumn(0, "COM_NUM", 
										dsPT_R_ASSI_TRANS_STATEMENT.GetColumn(0, "TRAN_COM_NUM"));
		dsPT_R_BASIC_CHANGE_HISTORY.SetColumn(0, "ADDR_ADDR", 
										dsPT_R_ASSI_TRANS_STATEMENT.GetColumn(0, "TRAN_ADDR"));
		dsPT_R_BASIC_CHANGE_HISTORY.SetColumn(0, "ADDR_POST_NUM", 
										dsPT_R_ASSI_TRANS_STATEMENT.GetColumn(0, "TRAN_POST_NUM"));
		dsPT_R_BASIC_CHANGE_HISTORY.SetColumn(0, "ADDR_DETAIL_ADDR", 
										dsPT_R_ASSI_TRANS_STATEMENT.GetColumn(0, "TRAN_DETAIL_ADDR"));
		
		//dsPT_R_BASIC_CHANGE_HISTORY.SetColumn(0, "TMP_WRT_NUM", _sKEY1);
		dsPT_R_BASIC_CHANGE_HISTORY.SetColumn(0, "COI_WRT_NUM", dsPT_R_ASSI_TRANS_STATEMENT.GetColumn(0, "COI_WRT_NUM"));
		dsPT_R_BASIC_CHANGE_HISTORY.SetColumn(0, "RECV_NUM", _sKEY2);
		dsPT_R_BASIC_CHANGE_HISTORY.SetColumn(0, "REC_DT", dsPT_R_ASSI_TRANS_STATEMENT.GetColumn(0, "RECV_DT"));
		dsPT_R_BASIC_CHANGE_HISTORY.SetColumn(0, "MOT_STE", "STD003");			
		dsPT_R_BASIC_CHANGE_HISTORY.SetColumn(0, "SND_STE", "X");
		dsPT_R_BASIC_CHANGE_HISTORY.SetColumn(0, "SIDO_CODE", global.g_Sd_cd);
		dsPT_R_BASIC_CHANGE_HISTORY.SetColumn(0, "WRT_ID", global.g_User_ID);
		dsPT_R_BASIC_CHANGE_HISTORY.SetColumn(0, "INS_DT", Today());
		
    	var sCODE1 = dsEXT_CHG_CLASS.GetColumn(0, "CODE1");
	    var sCODE2 = dsEXT_CHG_CLASS.GetColumn(0, "CODE2");
	    var sCODE3 = dsEXT_CHG_CLASS.GetColumn(0, "CODE3");
	    var sCODE4 = dsEXT_CHG_CLASS.GetColumn(0, "CODE4");
        var sReturn = "";
        sReturn += sCODE1;
        sReturn += sCODE2;
        sReturn += sCODE3;
        sReturn += sCODE4;
		dsPT_R_BASIC_CHANGE_HISTORY.SetColumn(0, "EXT_CHG_CLASS_CODE", sReturn);	
		//if(scTMP_WRT_NUM !='000000' && TRAN_GUBUN != 1)
		if(TRAN_GUBUN != 1)
		{
	     
		var _sKEY1 = dsPT_R_ASSI_TRANS_STATEMENT.GetColumn(0, "ASSI_TMP_WRT_NUM");
		dsPT_R_BASIC_CHANGE_HISTORY.SetColumn(0, "TMP_WRT_NUM", _sKEY1);
        dsPT_R_COMPANY_MASTER.SetColumn(0, "TMP_WRT_NUM", _sKEY1);
        //dsPT_R_COMPANY_MASTER.SetColumn(0, "TMP_WRT_NUM", _sKEY1);
        }
        if (ASSI_STE==3 && TRAN_GUBUN == 1)
        {
        
        var _sKEY1 = dsPT_R_ASSI_TRANS_STATEMENT.GetColumn(0, "TRAN_TMP_WRT_NUM");
        dsPT_R_BASIC_CHANGE_HISTORY.SetColumn(0, "TMP_WRT_NUM", _sKEY1);
        dsPT_R_COMPANY_MASTER.SetColumn(0, "TMP_WRT_NUM", _sKEY1);
		}
		if (ASSI_STE==3 && TRAN_GUBUN == 2)
        {
        var _sKEY1 = dsPT_R_ASSI_TRANS_STATEMENT.GetColumn(0, "ASSI_TMP_WRT_NUM");
		dsPT_R_BASIC_CHANGE_HISTORY.SetColumn(0, "TMP_WRT_NUM", _sKEY1);
        dsPT_R_COMPANY_MASTER.SetColumn(0, "TMP_WRT_NUM", _sKEY1);
		}
		if (fnEmpty(dsPT_R_ASSI_TRANS_STATEMENT.GetColumn(0, "BAS_STA_DT")))
		{
			dsPT_R_COMPANY_MASTER.SetColumn(0, "BAS_STA_DT", registDt);
			dsPT_R_BASIC_CHANGE_HISTORY.SetColumn(0, "BAS_STA_DT", registDt);
		}
		
		if (fnEmpty(dsPT_R_ASSI_TRANS_STATEMENT.GetColumn(0, "CHG_DT"))){
			dsPT_R_BASIC_CHANGE_HISTORY.SetColumn(0, "CHG_DT", registDt);
		}		
		
		for(var nLoop = 0; nLoop < dsPT_R_ENGINEER_CHANGE_TEMP.rowcount; nLoop++)
		{
			dsPT_R_ENGINEER_CHANGE_TEMP.SetColumn(nLoop, "EMPL_YMD", Today());
		}
		
		//기술자 입사일자를 등록일자로 자동반영
		for(var nLoop = 0; nLoop < dsPT_R_ENGINEER_CHANGE_TEMP.rowcount; nLoop++)
		{
			if( fnEmpty( dsPT_R_ENGINEER_CHANGE_TEMP.GetColumn(nLoop, "EMPL_YMD" ) ) )
				dsPT_R_ENGINEER_CHANGE_TEMP.SetColumn(nLoop, "EMPL_YMD", registDt);
		}
		
		//임원 입사일자를 등록일자로 자동반영
		for(var nLoop = 0; nLoop < dsPT_R_REFFER.rowcount; nLoop++)
		{
			if( fnEmpty( dsPT_R_REFFER.GetColumn(nLoop, "EMPL_YMD" ) ) )
				dsPT_R_REFFER.SetColumn(nLoop, "ENT_DT", registDt);
		}
		
	     /*   
        //세부변경 주석처리 하기
		var sArgs = " _parentType=Trn"+Trn+" _chgCode=" + dsPT_R_BASIC_CHANGE_HISTORY.GetColumn(0, "EXT_CHG_CLASS_CODE");
		sReturn = fnCreateDialog("cm::frmChgSelectDlg.xml", sArgs);	
		if(sReturn != null)
			dsPT_R_BASIC_CHANGE_HISTORY.SetColumn(0, "EXT_CHG_CLASS_CODE", sReturn);
			*/
	/*
		if (this.mot_ste.value == "STD003" ){	
			if (tbMain.tbAC.rdoAssi.Index == 0 && tbMain.tbAC.rdoTran.Index == 0 ){
		    var tmp_wrt_num_key = dsPT_R_ASSI_TRANS_STATEMENT.GetColumn(0,"TRAN_TMP_WRT_NUM");
		    alert("1 : "+tmp_wrt_num_key);
	} else {
		var tmp_wrt_num_key = dsPT_R_ASSI_TRANS_STATEMENT.GetColumn(0,"ASSI_TMP_WRT_NUM");
		alert("2 : "+tmp_wrt_num_key);
	       }
	}
	*/
	

		/*####################################################################*/
		//실적 이양(PT_R_WORK_RESULTS) // 시공능력 이양(PT_R_WORK_CAPABILITY)
		//공사업체간의 양도양수 처리시 작업을 해야 함
		/*####################################################################*/
		
		
		
	if(tbMain.tbAC.ASSI_STE.Value <> "1" 
	    and tbMain.tbAC.rdoAssi.index = 0 
	    and tbMain.tbAC.rdoTran.index = 0 )
		{
	
			dsPT_R_WORK_RESULTS_TEMP.DeleteAll();
			dsPT_R_WORK_CAPABILITY_TEMP.DeleteAll();
			
			http.sync = true;
			var sArgs = "workMODE=WORK_RES_CAP scRECV_NUM='"+this.RECV_NUM.value+"' ";// scTMP_WRT_NUM='"+dsPT_R_M_UNION_STATEMENT.GetColumn(0,"TMP_WRT_NUM")+"' ";
			var sTid = "WORK_RESULTS";
			var sUrl = "ICCS::cm/CmAction.do?cmd=TransMgrCN ";
			//var sInDs = "dsPT_R_ASSI_TRANS_STATEMENT=dsPT_R_ASSI_TRANS_STATEMENT:a ";
			var sInDs = "dsPT_R_TRANS_COMPANY_BEFORE=dsPT_R_TRANS_COMPANY_BEFORE:a ";
			var sOutDs = "dsWORK_RESULTS_TEMP=output dsWORK_CAPABILITY_TEMP=output2 dsRESCAP_TMP_WRT_NUM=output3";
			//var sOutDs = "dsWORK_RESULTS_TEMP=output dsWORK_CAPABILITY_TEMP=output2 ";
			Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);	
			http.sync = false;		
	
			//var set_tmp_wrt_num = dsPT_R_M_UNION_STATEMENT.GetColumn(0,"TMP_WRT_NUM");
			var set_tmp_wrt_num = dsPT_R_ASSI_TRANS_STATEMENT.GetColumn(0,"TMP_WRT_NUM");
			var cnt = 1;
			
			for (var sub=0; sub < dsWORK_RESULTS_TEMP.count; sub++)
			{
			
				fnSetMasterDetailMulti_Add("dsWORK_RESULTS_TEMP", "dsPT_R_WORK_RESULTS_TEMP",sub,sub);
				fnSetMasterDetailMulti_Add("dsWORK_RESULTS_TEMP", "dsPT_R_WORK_RESULTS",sub,sub);
				
				dsPT_R_WORK_RESULTS.SetColumn(sub,"TMP_WRT_NUM",set_tmp_wrt_num);
				dsPT_R_WORK_RESULTS.SetColumn(sub,"SEQ",cnt);
				
				dsPT_R_WORK_RESULTS_TEMP.SetColumn(sub,"SIDO_CODE",global.g_Sd_cd);
				dsPT_R_WORK_RESULTS_TEMP.SetColumn(sub,"WORK_PROC","T");			
				
				cnt = cnt + 1;
			}
			
			cnt = 1;
			for (var sub=0; sub < dsWORK_CAPABILITY_TEMP.count; sub++)
			{
			
				fnSetMasterDetailMulti_Add("dsWORK_CAPABILITY_TEMP", "dsPT_R_WORK_CAPABILITY_TEMP",sub,sub);
				fnSetMasterDetailMulti_Add("dsWORK_CAPABILITY_TEMP", "dsPT_R_WORK_CAPABILITY",sub,sub);
				
				dsPT_R_WORK_CAPABILITY.setColumn(sub,"TMP_WRT_NUM", set_tmp_wrt_num);			
				dsPT_R_WORK_CAPABILITY.setColumn(sub,"SEQ", cnt);
				
				dsPT_R_WORK_CAPABILITY_TEMP.SetColumn(sub,"SIDO_CODE",global.g_Sd_cd);
				dsPT_R_WORK_CAPABILITY_TEMP.SetColumn(sub,"CAP_PROC","T");	
				
				cnt = cnt + 1;
			}
		}
		
		

		/*####################################################################*/
		//실적 이양(PT_R_WORK_RESULTS) // 시공능력 이양(PT_R_WORK_CAPABILITY)
		//공사업체간의 양도양수 처리시 작업을 해야 함
		/*####################################################################*/
		
	}
	
	var sArgs = "workMODE=ALL scRECV_NUM="+ _sKEY2;
	var sTid = "SAVE";
	var sUrl = "ICCS::cm/CmAction.do?cmd=TransMgrCCUDN";
	var sInDs = " dsPT_R_MOT_STE=dsPT_R_MOT_STE:u dsPT_R_ASSI_TRANS_STATEMENT=dsPT_R_ASSI_TRANS_STATEMENT:u ";
	    sInDs += " dsPT_R_TRANS_COMPANY_BEFORE=dsPT_R_TRANS_COMPANY_BEFORE:u dsPT_R_ENGINEER_CHANGE_TEMP=dsPT_R_ENGINEER_CHANGE_TEMP:u ";	
	var sOutDs = "";
	var sCallBack = "fn_CallBack";
	/**
	*	처리 상태 완료
	**/
	if (this.mot_ste.value == "STD003" ){
		sInDs += " dsPT_R_COMPANY_MASTER=dsPT_R_COMPANY_MASTER:u dsPT_R_BASIC_CHANGE_HISTORY=dsPT_R_BASIC_CHANGE_HISTORY:u ";
		sInDs += " dsPT_R_ENGINEER_CHANGE=dsPT_R_ENGINEER_CHANGE:u  ";
	
	}	
	
	Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);	
	
	
		if (this.mot_ste.value == "STD003" ){
		dsPT_TMP_TRANS_KEY.DeleteAll();
		dsPT_TMP_TRANS_KEY.AddRow();
		
    var TRAN_GUBUN = dsPT_R_ASSI_TRANS_STATEMENT.GetColumn(0, "TRAN_GUBUN");
    if (TRAN_GUBUN == "1" ){
    dsPT_TMP_TRANS_KEY.SetColumn(dsPT_TMP_TRANS_KEY.row, "KEY1", dsPT_R_ASSI_TRANS_STATEMENT.GetColumn(0,"TRAN_TMP_WRT_NUM"));
     
    }
      if(TRAN_GUBUN <> "1" ){
       dsPT_TMP_TRANS_KEY.SetColumn(dsPT_TMP_TRANS_KEY.row, "KEY1",dsPT_R_ASSI_TRANS_STATEMENT.GetColumn(0,"ASSI_TMP_WRT_NUM"));
         
       }
                                           
	   	// 등록완료시 협회송신 큐에 넣기
		
		
		dsPT_TMP_TRANS_KEY.SetColumn(dsPT_TMP_TRANS_KEY.row, "WORK_CLASS_CODE", 
					dsPT_R_BASIC_CHANGE_HISTORY.GetColumn(0, "STA_CHG_CLASS_CODE"));	
		dsPT_TMP_TRANS_KEY.SetColumn(dsPT_TMP_TRANS_KEY.row, "KEY2", 
		            dsPT_R_ASSI_TRANS_STATEMENT.GetColumn(0, "RECV_NUM"));
		dsPT_TMP_TRANS_KEY.SetColumn(dsPT_TMP_TRANS_KEY.row, "KEY3", 
		            dsPT_R_ASSI_TRANS_STATEMENT.GetColumn(0, "SIDO_CODE"));
		dsPT_TMP_TRANS_KEY.SetColumn(dsPT_TMP_TRANS_KEY.row, "KEY4", 
		            dsPT_R_ASSI_TRANS_STATEMENT.GetColumn(0, "COI_WRT_NUM"));
		if(dsPT_R_ASSI_TRANS_STATEMENT.GetColumn(0, "SND_STE") == "O")
			dsPT_TMP_TRANS_KEY.SetColumn(dsPT_TMP_TRANS_KEY.row, "CUD_FLAG", "U");
		else
			dsPT_TMP_TRANS_KEY.SetColumn(dsPT_TMP_TRANS_KEY.row, "CUD_FLAG", "C");
		
		dsPT_TMP_TRANS_KEY.SetColumn(dsPT_TMP_TRANS_KEY.row, "SIDO_CODE", global.g_Sd_cd);
		dsPT_TMP_TRANS_KEY.SetColumn(dsPT_TMP_TRANS_KEY.row, "INS_DT", Today());
		
		http.Sync = true;
		var sArgs = "";		
		var sTid = "KICAIfSendCUD";
		var sUrl = "ICCS::sys_m/Sys_mAction.do?cmd=KICAIfSendCUD";
		var sInDs = "dsPT_TMP_TRANS_KEY=dsPT_TMP_TRANS_KEY:u";
		var sOutDs = "dsPT_TMP_TRANS_KEY=output";
		var sCallBack = "fn_CallBack";
		
		Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
		http.Sync = false;
	}
}


//////////////////////////////////////////////////////////////////
// 겸헙사항 Detail Event
//////////////////////////////////////////////////////////////////
function fnCompanyDetailButton_Click(obj)
{
	var sDtWorkFlag;

	if(obj == tbMain.tbInfo.btnCInsert)	
	{
	 var _sKEY1 = dsPT_R_ASSI_TRANS_STATEMENT.GetColumn(0, "ASSI_TMP_WRT_NUM");

		sDtWorkFlag = global.gv_INSERT;
		dsPT_R_SUBSIDIARY.AddRow();
		dsPT_R_SUBSIDIARY.SetColumn(dsPT_R_SUBSIDIARY.row, "TMP_WRT_NUM", _sKEY1);
		dsPT_R_SUBSIDIARY.SetColumn(dsPT_R_SUBSIDIARY.row, "SEQ", dsPT_R_SUBSIDIARY.rowcount);
		fnInitCompanyGroup(sDtWorkFlag);
	}
	else if (obj == tbMain.tbInfo.btnCModify)
	{
		sDtWorkFlag = global.gv_MODIFY;
		fnInitCompanyGroup(sDtWorkFlag);
	}
	else if (obj == tbMain.tbInfo.btnCRemove)
	{
		sDtWorkFlag = global.gv_INQUIRY;
		
		dsPT_R_SUBSIDIARY.ApplyChange();
		var nCount = fnGridRowRemove(dsPT_R_SUBSIDIARY);
		
		if(nCount == 0)
		{
			alert('삭제할 데이터를 선택하세요.');
			return;
		}
		var sArgs = "workMODE=SUBSIDIARY";	
		var sTid = "D_SUBSIDIARY";
		var sUrl = "ICCS::cm/CmAction.do?cmd=TransMgrCCUDN";
		var sInDs = "dsPT_R_SUBSIDIARY=dsPT_R_SUBSIDIARY:u";
		var sOutDs = "";
		var sCallBack = "fn_CallBack";

		Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
	}
	else if (obj == tbMain.tbInfo.btnCCancel)
	{
		if (dsPT_R_SUBSIDIARY.GetRowType(dsPT_R_SUBSIDIARY.row) == "insert")
			dsPT_R_SUBSIDIARY.DeleteRow(dsPT_R_SUBSIDIARY.row);
		else
			fnRejectChange(dsPT_R_SUBSIDIARY);
		
		sDtWorkFlag = global.gv_INQUIRY;
		fnInitCompanyGroup(sDtWorkFlag);		
	}
	else if (obj == tbMain.tbInfo.btnCSave)
	{
			if( fnEmpty(dsPT_R_SUBSIDIARY.GetColumn(dsPT_R_SUBSIDIARY.row, "SUB_WRT_NUM")) ) {
			alert( "등록번호를 입력하세요" );
			return;
		}
		
		 //겹업 중복체크 석곤 2011-06-29
    	var rtn;
    	for(i=0;i<dsPT_R_SUBSIDIARY.count;i++){
	    rtn = dsPT_R_SUBSIDIARY.SearchRow(" SUB_WRT_NUM = '" + dsPT_R_SUBSIDIARY.GetColumn(i,"SUB_WRT_NUM")+ "' &&  SUB_NAME = '" + dsPT_R_SUBSIDIARY.GetColumn(i,"SUB_NAME")+ "'",i+1,dsPT_R_SUBSIDIARY.count);
          if(rtn != -1 ){ //같은 값이 있을때
            alert("중복 입력 하였습니다");
            dsPT_R_SUBSIDIARY.DeleteRow(dsPT_R_SUBSIDIARY.row);
            
            sDtWorkFlag = global.gv_INQUIRY;
		    fnInitCompanyGroup(sDtWorkFlag);
            
            return false;
            break;
           }
         }
		var sArgs = "workMODE=SUBSIDIARY";	
		var sTid = "I_SUBSIDIARY";
		var sUrl = "ICCS::cm/CmAction.do?cmd=TransMgrCCUDN";
		var sInDs = "dsPT_R_SUBSIDIARY=dsPT_R_SUBSIDIARY:u";
		var sOutDs = "";
		var sCallBack = "fn_CallBack";
		
		Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
	}
}

function fnInitCompanyGroup(sDtWorkFlag)
{
	if(sDtWorkFlag == global.gv_INQUIRY)
	{
		fnControlState(false, tbMain.tbInfo, "TMPSUB_CONT,SUB_WRT_NUM,btnComDivCode");
		fnControlState(true, tbMain.tbInfo, "grdCOMPANY");
		
		tbMain.tbInfo.btnCInsert.Enable = true;
		tbMain.tbInfo.btnCModify.Enable = true;
		tbMain.tbInfo.btnCRemove.Enable = true;
		tbMain.tbInfo.btnCSave.Enable = false;
		tbMain.tbInfo.btnCCancel.Enable = false;
	}
	else if(sDtWorkFlag == global.gv_INSERT)
	{
		fnControlState(true, tbMain.tbInfo, "TMPSUB_CONT,SUB_WRT_NUM,btnComDivCode");
		fnControlState(false, tbMain.tbInfo, "grdCOMPANY");
		
		tbMain.tbInfo.btnCInsert.Enable = false;
		tbMain.tbInfo.btnCModify.Enable = false;
		tbMain.tbInfo.btnCRemove.Enable = false;
		tbMain.tbInfo.btnCSave.Enable = true;
		tbMain.tbInfo.btnCCancel.Enable = true;
	}
	else if(sDtWorkFlag == global.gv_MODIFY)
	{
		fnControlState(true, tbMain.tbInfo, "TMPSUB_CONT,SUB_WRT_NUM,btnComDivCode");
		fnControlState(false, tbMain.tbInfo, "grdCOMPANY");
		
		tbMain.tbInfo.btnCInsert.Enable = false;
		tbMain.tbInfo.btnCModify.Enable = false;
		tbMain.tbInfo.btnCRemove.Enable = false;
		tbMain.tbInfo.btnCSave.Enable = true;
		tbMain.tbInfo.btnCCancel.Enable = true;
	}
}


function fnInitRefusalGroup(sDtWorkFlag)
{

	if(sDtWorkFlag == global.gv_INQUIRY)
	{
	
		//fnAllControlState(false, tbMain.tbEtc);
		//fnControlState(true, tbMain.tbEtc, "grdRefusal");
		fnControlState(false, tbMain.tbEtc, "REFUSAL_DIV,REFUSAL_CONTENT");
		fnControlState(true, tbMain.tbEtc, "grdRefusal, btnReperInsert, btnReperModify, btnReperRemove, MEMO_CONT, Edit2, Button2, Radio0, Radio2, Radio3");

		tbMain.tbEtc.btnRefusalInsert.Enable = true;
		tbMain.tbEtc.btnRefusalModify.Enable = true;
		tbMain.tbEtc.btnRefusalRemove.Enable = true;
		tbMain.tbEtc.btnRefusalSave.Enable = false;
		tbMain.tbEtc.btnRefusalCancel.Enable = false;
		
	}
	else if(sDtWorkFlag == global.gv_INSERT)
	{
	
		//fnAllControlState(true, tbMain.tbEtc);
		fnControlState(false, tbMain.tbEtc, "grdRefusal");
		fnControlState(true, tbMain.tbEtc, "REFUSAL_DIV,REFUSAL_CONTENT");
		
		tbMain.tbEtc.btnRefusalInsert.Enable = false;
		tbMain.tbEtc.btnRefusalModify.Enable = false;
		tbMain.tbEtc.btnRefusalRemove.Enable = false;
		tbMain.tbEtc.btnRefusalSave.Enable = true;
		tbMain.tbEtc.btnRefusalCancel.Enable = true;
		
	}
	else if(sDtWorkFlag == global.gv_MODIFY)
	{
	
		//fnAllControlState(true, tbMain.tbEtc);
		fnControlState(false, tbMain.tbEtc, "grdRefusal");		
		fnControlState(true, tbMain.tbEtc,"REFUSAL_DIV,REFUSAL_CONTENT");
		
		tbMain.tbEtc.btnRefusalInsert.Enable = false;
		tbMain.tbEtc.btnRefusalModify.Enable = false;
		tbMain.tbEtc.btnRefusalRemove.Enable = false;
		tbMain.tbEtc.btnRefusalSave.Enable = true;
		tbMain.tbEtc.btnRefusalCancel.Enable = true;
				
	}
}

//////////////////////////////////////////////////////////////////
// 신원조회 관련 처리 - 임원정보
//////////////////////////////////////////////////////////////////
function fnReperDetailButton_Click(obj)
{
	var sDtWorkFlag;
	
	if(obj == tbMain.tbEtc.btnReperInsert)
	{
	    if(tbMain.tbAC.rdoTran.index == 1){
	        var _sKEY1 = dsPT_R_ASSI_TRANS_STATEMENT.GetColumn(0,"ASSI_TMP_WRT_NUM");
	    }
	    else{
	        var _sKEY1 = dsPT_R_ASSI_TRANS_STATEMENT.GetColumn(0,"TRAN_TMP_WRT_NUM");
	    }
	

		
		http.Sync = true;
		var sArgs = "workMODE=GET_NEXT_SEQ_REFFER scTMP_WRT_NUM="+_sKEY1;
		var sTid = "GET_NEXT_SEQ_REFFER";
		var sUrl = "ICCS::cm/CmAction.do?cmd=TransMgrCN";
		var sOutDs = "dsENGINEER_SEQ=output";
		var sCallBack = "fn_CallBack";
		
		Transaction(sTid, sUrl, "", sOutDs, sArgs, sCallBack);
		
		http.Sync = false;
		
		if( dsENGINEER_SEQ == null || dsENGINEER_SEQ.rowcount == 0 ) {
			alert( "시스템오류 : 임원을 추가할 수 없습니다." );
			return;
		}
		
		var nextSeqEngineer = dsENGINEER_SEQ.getColumn( 0, 0 );
		
		sDtWorkFlag = global.gv_INSERT;
		dsPT_R_REFFER.AddRow();
		dsPT_R_REFFER.SetColumn(dsPT_R_REFFER.row, "TMP_WRT_NUM", _sKEY1);
		dsPT_R_REFFER.SetColumn(dsPT_R_REFFER.row, "SEQ", nextSeqEngineer);
		dsPT_R_REFFER.SetColumn(dsPT_R_REFFER.row, "SI_DO_CODE", global.g_Sd_cd);
		 
		var scFOREIGN_YN=dsPT_R_REFFER.GetColumn(dsPT_R_REFFER.row, "FOREIGN_YN");
		 if (scFOREIGN_YN ==null  )
		{
		this.tbMain.tbEtc.FOREIGN_YN.Value = "N";		   
		}
		 if (dsPT_R_REFFER.row == 0 )
		{
		this.tbMain.tbEtc.NM_KOR.Value = this.tbMain.tbInfo.REP_NAME_KOR.Value;
		this.tbMain.tbEtc.NAME_HANJA.Value = this.tbMain.tpInfo.Edit1.Value;
		this.tbMain.tbEtc.SSN1.Value = this.tbMain.tbInfo.REP_SSN1.Value;
		this.tbMain.tbEtc.SSN2.Value = this.tbMain.tbInfo.Edit7.Value;
		//this.tbMain.tpEtc.Edit0.Value = this.tbMain.tpInfo.ADDR_ADDR.Value;
		   
		}
		
		fnInitReperGroup(sDtWorkFlag);
	}
	else if (obj == tbMain.tbEtc.btnReperModify)
	{
		sDtWorkFlag = global.gv_MODIFY;
		fnInitReperGroup(sDtWorkFlag);
	}
	else if (obj == tbMain.tbEtc.btnReperRemove)
	{
		sDtWorkFlag = global.gv_INQUIRY;
		
		dsPT_R_REFFER.ApplyChange();
		var nCount = fnGridRowRemove(dsPT_R_REFFER);
		
		if(nCount == 0)
		{
			alert('삭제할 데이터를 선택하세요.');
			return;
		}
		var sArgs = "workMODE=REPER";	
		var sTid = "REPER_DELETE";
		var sUrl = "ICCS::cm/CmAction.do?cmd=TransMgrCCUD";
		var sInDs = "dsPT_R_REFFER=dsPT_R_REFFER:u";
		var sOutDs = "dsPT_R_REFFER=output";
		var sCallBack = "fn_CallBack";

		Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
	}
	else if (obj == tbMain.tbEtc.btnReperCancel)
	{
		if (dsPT_R_REFFER.GetRowType(dsPT_R_REFFER.row) == "insert")
			dsPT_R_REFFER.DeleteRow(dsPT_R_REFFER.row);
		else
			fnRejectChange(dsPT_R_REFFER);
				
		sDtWorkFlag = global.gv_INQUIRY;
		fnInitReperGroup(sDtWorkFlag);
	}
	else if (obj == tbMain.tbEtc.btnReperSave)
	{
		var sArgs = "workMODE=REPER scTMP_WRT_NUM="+ _sKEY1;
		var sTid = "REPER_SAVE";
		var sUrl = "ICCS::cm/CmAction.do?cmd=TransMgrCCUDN";
		var sInDs = "dsPT_R_REFFER=dsPT_R_REFFER:u";
		var sOutDs = "dsPT_R_REFFER=output";
		var sCallBack = "fn_CallBack";
		
		Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
	}
}

function fnInitReperGroup(sDtWorkFlag)
{
	
	if(sDtWorkFlag == global.gv_INQUIRY)
	{
		fnControlState(false, tbMain.tbEtc, "NM_KOR, NAME_HANJA, SSN1, SSN2, ISSUE_DATE, GUBUN, FOREIGN_YN, ORIGIN, REMARK");
		fnControlState(true, tbMain.tbEtc, "grdReper, btnReper");
		
		tbMain.tbEtc.btnReperInsert.Enable = true;
		tbMain.tbEtc.btnReperModify.Enable = true;
		tbMain.tbEtc.btnReperRemove.Enable = true;
		tbMain.tbEtc.btnReperSave.Enable = false;
		tbMain.tbEtc.btnReperCancel.Enable = false;
	}
	else if(sDtWorkFlag == global.gv_INSERT)
	{
		fnControlState(true, tbMain.tbEtc, "NM_KOR, NAME_HANJA, SSN1, SSN2, ISSUE_DATE, GUBUN, FOREIGN_YN, ORIGIN, REMARK");
		fnControlState(false, tbMain.tbEtc, "grdReper");
		
		tbMain.tbEtc.btnReperInsert.Enable = false;
		tbMain.tbEtc.btnReperModify.Enable = false;
		tbMain.tbEtc.btnReperRemove.Enable = false;
		tbMain.tbEtc.btnReperSave.Enable = true;
		tbMain.tbEtc.btnReperCancel.Enable = true;
	}
	else if(sDtWorkFlag == global.gv_MODIFY)
	{		
		fnControlState(true, tbMain.tbEtc, "NM_KOR, NAME_HANJA, SSN1, SSN2, ISSUE_DATE, GUBUN, FOREIGN_YN, ORIGIN, REMARK");
		fnControlState(false, tbMain.tbEtc, "grdReper");
		
		tbMain.tbEtc.btnReperInsert.Enable = false;
		tbMain.tbEtc.btnReperModify.Enable = false;
		tbMain.tbEtc.btnReperRemove.Enable = false;
		tbMain.tbEtc.btnReperSave.Enable = true;
		tbMain.tbEtc.btnReperCancel.Enable = true;
	}
}

