function fnEngineerButton_Click(obj)
{
	var sDtWorkFlag;
	var tmp_wrt_num_key = "";
	
	var _sKEY1 = dsPT_R_ASSI_TRANS_STATEMENT.GetColumn(dsPT_R_ASSI_TRANS_STATEMENT.row, "TMP_WRT_NUM");
    var _sKEY2 = dsPT_R_ASSI_TRANS_STATEMENT.GetColumn(dsPT_R_ASSI_TRANS_STATEMENT.row, "RECV_NUM");


	/***
		FUNCTION 으로 돌려야 함
	*/
	if (tbMain.tbAC.rdoAssi.Index == 1 && tbMain.tbAC.rdoTran.Index == 0 ){
		tmp_wrt_num_key = dsPT_R_ASSI_TRANS_STATEMENT.GetColumn(0,"TRAN_TMP_WRT_NUM");
	} else {
		tmp_wrt_num_key = dsPT_R_ASSI_TRANS_STATEMENT.GetColumn(0,"ASSI_TMP_WRT_NUM");
	}
	
	if(obj == tbMain.tb_Engineer.btnEngInsert)
	{
		http.Sync = true;
		var sArgs = "workMODE=GET_NEXT_SEQ_ENGINEER scRECV_NUM="+ _sKEY2;
		var sTid = "GET_NEXT_SEQ_ENGINEER";
		var sUrl = "ICCS::cm/CmAction.do?cmd=TransMgrCN";
		var sOutDs = "dsENGINEER_SEQ=output";
		var sCallBack = "fn_CallBack";
		
		Transaction(sTid, sUrl, "", sOutDs, sArgs, sCallBack);
		
		http.Sync = false;
		
		if( dsENGINEER_SEQ == null || dsENGINEER_SEQ.rowcount == 0 ) {
			alert( "시스템오류 : 기술자를 추가할 수 없습니다." );
			return;
		}
		
		var nextSeqEngineer = dsENGINEER_SEQ.getColumn( 0, 0 );

		sDtWorkFlag = global.gv_INSERT;
		dsPT_R_ENGINEER_CHANGE_TEMP.AddRow();
		
		dsPT_R_ENGINEER_CHANGE_TEMP.SetColumn(this.dsPT_R_ENGINEER_CHANGE_TEMP.row, "TMP_WRT_NUM", tmp_wrt_num_key);
		dsPT_R_ENGINEER_CHANGE_TEMP.SetColumn(this.dsPT_R_ENGINEER_CHANGE_TEMP.row, "SEQ", nextSeqEngineer );
		dsPT_R_ENGINEER_CHANGE_TEMP.SetColumn(this.dsPT_R_ENGINEER_CHANGE_TEMP.row, "WRT_ID", global.g_User_ID);
		dsPT_R_ENGINEER_CHANGE_TEMP.SetColumn(this.dsPT_R_ENGINEER_CHANGE_TEMP.row, "ENG_PROC", "T");		
		dsPT_R_ENGINEER_CHANGE_TEMP.SetColumn(this.dsPT_R_ENGINEER_CHANGE_TEMP.row, "ENG_TARGET", "AS");
		dsPT_R_ENGINEER_CHANGE_TEMP.SetColumn(this.dsPT_R_ENGINEER_CHANGE_TEMP.row, "RECV_NUM", _sKEY2);
		dsPT_R_ENGINEER_CHANGE_TEMP.SetColumn(this.dsPT_R_ENGINEER_CHANGE_TEMP.row, "RECV_TEMP", _sKEY2);
		dsPT_R_ENGINEER_CHANGE_TEMP.SetColumn(this.dsPT_R_ENGINEER_CHANGE_TEMP.row, "SIDO_CODE",global.g_Sd_cd );
		
		
		fnInitEngineerGroup(sDtWorkFlag);
		
		tbMain.tb_Engineer.ENGINEER_SSN1.SetFocus();
	}
	else if (obj == tbMain.tb_Engineer.btnEngModify)
	{
		sDtWorkFlag = global.gv_MODIFY;
		fnInitEngineerGroup(sDtWorkFlag);
	}
	else if (obj == tbMain.tb_Engineer.btnEngRemove)
	{
		sDtWorkFlag = global.gv_INQUIRY;
		
		dsPT_R_ENGINEER_CHANGE_TEMP.ApplyChange();
		var nCount = fnGridRowRemove(dsPT_R_ENGINEER_CHANGE_TEMP);
		
		if(nCount == 0)
		{
			alert('삭제할 데이터를 선택하세요.');
			return;
		}
		var _sKEY2 = dsPT_R_ASSI_TRANS_STATEMENT.GetColumn(0,"RECV_NUM");
		var sArgs = "workMODE=ENGINEER scRECV_NUM="+ _sKEY2;
		var sTid = "D_ENGINEER";
		var sUrl = "ICCS::cm/CmAction.do?cmd=TransMgrCCUDN";
		var sInDs = "dsPT_R_ENGINEER_CHANGE_TEMP=dsPT_R_ENGINEER_CHANGE_TEMP:u ";
		var sOutDs = "";
		var sCallBack = "fn_CallBack";

		Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
	}
	else if (obj == tbMain.tb_Engineer.btnEngCancel)
	{
		if (dsPT_R_ENGINEER_CHANGE_TEMP.GetRowType(dsPT_R_ENGINEER_CHANGE_TEMP.row) == "insert")
			dsPT_R_ENGINEER_CHANGE_TEMP.DeleteRow(dsPT_R_ENGINEER_CHANGE_TEMP.row);
		else
			fnRejectChange(dsPT_R_ENGINEER_CHANGE_TEMP);
		
		sDtWorkFlag = global.gv_INQUIRY;
		fnInitEngineerGroup(sDtWorkFlag);
	}
	else if (obj == tbMain.tb_Engineer.btnEngSave)
	{
	if(fnEmpty(tbMain.tb_Engineer.ENGINEER_SSN1.Text) || fnEmpty(tbMain.tb_Engineer.ENGINEER_SSN2.Text) )
	{
		alert('주민등록번호를 입력하세요.');
		tbMain.tb_Engineer.ENGINEER_SSN1.SetFocus();
		return;
	}
//기술자 중복체크
	else if(fnEmpty(tbMain.tb_Engineer.EMPL_YMD.Value))
	{
	alert('입사일자를 입력하세요.');
	tbMain.tb_Engineer.EMPL_YMD.SetFocus();
	return;
	}//입사일자 입력체크 20120829 서춘교
	
var rtn;

for(i=0;i<dsPT_R_ENGINEER_CHANGE_TEMP.count;i++)
{
rtn = dsPT_R_ENGINEER_CHANGE_TEMP.SearchRow(" ENGINEER_SSN1 = '" + dsPT_R_ENGINEER_CHANGE_TEMP.GetColumn(i,"ENGINEER_SSN1")+ "' &&  ENGINEER_SSN2 = '" + dsPT_R_ENGINEER_CHANGE_TEMP.GetColumn(i,"ENGINEER_SSN2")+ "'",i+1,dsPT_R_ENGINEER_CHANGE_TEMP.count);
 if(rtn != -1 ) //같은 값이 있을때
 {
 alert("중복 입력 하였습니다");
 dsPT_R_ENGINEER_CHANGE_TEMP.DeleteRow(dsPT_R_ENGINEER_CHANGE_TEMP.row);
 sDtWorkFlag = global.gv_INQUIRY;
		fnInitEngineerGroup(sDtWorkFlag);
 return false;
 break;
 }
}			
		var _sKEY2 = dsPT_R_ASSI_TRANS_STATEMENT.GetColumn(0,"RECV_NUM");

		var sArgs = "workMODE=ENGINEER scRECV_NUM="+ _sKEY2;
		var sTid = "ENGINEER_SAVE";
		var sUrl = "ICCS::cm/CmAction.do?cmd=TransMgrCCUDN";
		var sInDs = "dsPT_R_ENGINEER_CHANGE_TEMP=dsPT_R_ENGINEER_CHANGE_TEMP:u";
		var sOutDs = "";
		var sCallBack = "fn_CallBack";
		
		Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
	}
}

function fnInitEngineerGroup(sDtWorkFlag)
{
	if(sDtWorkFlag == global.gv_INQUIRY)
	{
		fnAllControlState(false, tbMain.tb_Engineer);
		fnControlState(true, tbMain.tb_Engineer, "grdEngineer,grdEngCheck");

		tbMain.tb_Engineer.btnEngInsert.Enable = true;
		tbMain.tb_Engineer.btnEngModify.Enable = true;
		tbMain.tb_Engineer.btnEngRemove.Enable = true;
		tbMain.tb_Engineer.btnEngSave.Enable = false;
		tbMain.tb_Engineer.btnEngCancel.Enable = false;
	}
	else if(sDtWorkFlag == global.gv_INSERT)
	{
		//fnAllControlState(false, tbMain.tb_Engineer);
		//fnControlState(true, tbMain.tb_Engineer, "ENGINEER_SSN1,ENGINEER_SSN2,btnEngineer");
		//fnControlState(true, tbMain.tb_Engineer, "ENGINEER_SSN1,ENGINEER_SSN2,ENGINEER_NM,TMP_FIELD,TMP_BOOK_ISSUE_NUM,EMPL_YMD,RET_YMD");
		
		fnAllControlState(false, tbMain.tb_Engineer);
		fnAllControlState(true, tbMain.tb_Engineer);
		
		tbMain.tb_Engineer.btnEngInsert.Enable = false;
		tbMain.tb_Engineer.btnEngModify.Enable = false;
		tbMain.tb_Engineer.btnEngRemove.Enable = false;
		tbMain.tb_Engineer.btnEngSave.Enable = true;
		tbMain.tb_Engineer.btnEngCancel.Enable = true;
	}
	else if(sDtWorkFlag == global.gv_MODIFY)
	{
		fnAllControlState(false, tbMain.tb_Engineer);
		fnControlState(true, tbMain.tb_Engineer, "ENGINEER_SSN1,ENGINEER_SSN2,btnEngineer");
		fnControlState(true, tbMain.tb_Engineer, "ENGINEER_SSN1,ENGINEER_SSN2,ENGINEER_NM,TMP_FIELD,TMP_BOOK_ISSUE_NUM,EMPL_YMD,RET_YMD");
		tbMain.tb_Engineer.btnEngInsert.Enable = false;
		tbMain.tb_Engineer.btnEngModify.Enable = false;
		tbMain.tb_Engineer.btnEngRemove.Enable = false;
		tbMain.tb_Engineer.btnEngSave.Enable = true;
		tbMain.tb_Engineer.btnEngCancel.Enable = true;
	}
}

function tbMain_tbEtc_btnReperReport_OnClick(obj)
{
	for (var nLoop = 0; nLoop < dsPT_R_REFFER.rowcount; nLoop++)
	{
		dsPT_R_REFFER.SetColumn(nLoop, "SEND_YN", "Y");
		dsPT_R_REFFER.SetColumn(nLoop, "CHAR_REF_YN", "2");
	}
		
	var sArgs = "workMODE=REPER";
	var sTid = "REPER_REPORT";
	var sUrl = "ICCS::cm/CmAction.do?cmd=RegMgrCCUD";
	var sInDs = "dsPT_R_REFFER=dsPT_R_REFFER:u";
	var sOutDs = "dsPT_R_REFFER=output";
	var sCallBack = "fn_CallBack";
	
	Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
}

function fnReferReport()
{
	var sViewer = "_sViewer=PersonInspect";
	var sReportname =  " _sReportname=connection.reportname=/BusinessRegist/PersonInspect.ozr";
	var sOdiNames = " _sOdiNames=odi.odinames=Q)PersonInspect";
	var	spCount = " _spCount=odi.Q)PersonInspect.pcount=1";
	var sParam = " _sParam=odi.Q)PersonInspect.args1=TMP_WRT_NUM=" + _sKEY1;
	
	var sRet = fnCreateDialog("bas::frmReport.xml", sViewer + sReportname + sOdiNames + spCount + sParam);	

}

//////////////////////////////////////////////////////////////////
// Master Button Click Event
//////////////////////////////////////////////////////////////////
/**

 1. 함수명 : fnMasterButton_Click
 2. 기  능 : 각 화면의 Master Button click 처리
 3. Params 
    @obj : Button object
 4. Return
    없음
*/
function fnMasterButton_Click(obj)
{

	if (obj == tbMain.tbAC.btnCorp || obj == tbMain.tbAC.btnCorp2){ // 양도양수자 공사업 선택
		var sArgs = "_sOldCode=" + "";
		var sRetVal = fnCreateDialog("bas::CMStD.xml", sArgs);
		
		if(sRetVal == null) return;
		
		var OLD_TMP_WRT_NUM =  "";
		
		if (dsPT_R_ASSI_TRANS_STATEMENT.GetColumn(0,"ASSI_GUBUN") == "0" ||
			dsPT_R_ASSI_TRANS_STATEMENT.GetColumn(0,"TRAN_GUBUN") == "1" )
		{
			OLD_TMP_WRT_NUM = dsPT_R_ASSI_TRANS_STATEMENT.GetColumn(0,"TRAN_TMP_WRT_NUM");
		} else {
			OLD_TMP_WRT_NUM = dsPT_R_ASSI_TRANS_STATEMENT.GetColumn(0,"ASSI_TMP_WRT_NUM");
		}

		dsENGINEER_CHANGE_TEMP.DeleteAll();
		//dsPT_R_ENGINEER_CHANGE_TEMP.DeleteAll();
				
		var sArgs = "scTMP_WRT_NUM=" + sRetVal[1]   + " workMODE=GET_CORP";
		sArgs += " scRECV_NUM='"+this.RECV_NUM.value + "' scOLD_TMP_WRT_NUM='"+OLD_TMP_WRT_NUM+"'";
		sArgs += " scSIDO_CODE='"+global.g_Sd_cd+"' ";
		var sTid = "";
		var sInDs = "";		
		var sOutDs;
		
		
		if(obj == tbMain.tbAC.btnCorp) {
			sTid = "GET_CORP";
			sArgs += " scENG_TARGET=AS"; //양도업체
		} else if(obj == tbMain.tbAC.btnCorp2) {
			sTid = "GET_CORP2";
			sArgs += " scENG_TARGET=TR"; //양수업체
		}
		
		sOutDs = "dsCOMPANY_MASTER=output dsENGINEER_CHANGE_TEMP=output2 dsMAX_SEQ=output3 ";

		if (tbMain.tbAC.ASSI_STE.Index == 3){
			sOutDs += " dsPT_R_SUBSIDIARY=output4";
		} 

		var sUrl = "ICCS::cm/CmAction.do?cmd=TransMgrCN";		
		var sCallBack = "fn_CallBack";
		
		Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
	}
	
	if (obj ==  tbMain.tbAC.btnGetPost)			// 우편번호
	{
		var sReturn = fnCreateDialog("bas::PostStD.xml");
		//tbMain.tbAC.TRAN_POST_NUM.value = sReturn[0].replace("-","");
		//tbMain.tbAC.TRAN_ADDR.value = SubStr(sReturn[1],0,IndexOf(sReturn[1],'동')+1);
		//tbMain.tbAC.TRAN_DETAIL_ADDR.value = SubStr(sReturn[1],IndexOf(sReturn[1],'동')+1,sReturn[1].length);
		dsPT_R_ASSI_TRANS_STATEMENT.SetColumn(dsPT_R_ASSI_TRANS_STATEMENT.row, "TRAN_POST_NUM", sReturn[0].replace("-",""));
		dsPT_R_ASSI_TRANS_STATEMENT.SetColumn(dsPT_R_ASSI_TRANS_STATEMENT.row, "TRAN_ADDR", sReturn[1]);
		dsPT_R_ASSI_TRANS_STATEMENT.SetColumn(dsPT_R_ASSI_TRANS_STATEMENT.row, "TRAN_DETAIL_ADDR", sReturn[2]);
		//tbMain.tbAC.TRAN_DETAIL_ADDR.Text = "";
		tbMain.tbAC.TRAN_DETAIL_ADDR.SetFocus();
		
	}
	
		if (obj ==  tbMain.tbInfo.btnGetPost2)			// 우편번호
	{
		var sReturn = fnCreateDialog("bas::PostStD.xml");
		//tbMain.tbInfo.ADDR_POST_NUM.value = sReturn[0].replace("-","");
		//tbMain.tbInfo.ADDR_ADDR.value = SubStr(sReturn[1],0,IndexOf(sReturn[1],'동')+1);
		//tbMain.tbInfo.ADDR_DETAIL_ADDR.value = SubStr(sReturn[1],IndexOf(sReturn[1],'동')+1,sReturn[1].length);
		dsPT_R_ASSI_TRANS_STATEMENT.SetColumn(dsPT_R_ASSI_TRANS_STATEMENT.row, "ADDR_POST_NUM", sReturn[0].replace("-",""));
		dsPT_R_ASSI_TRANS_STATEMENT.SetColumn(dsPT_R_ASSI_TRANS_STATEMENT.row, "ADDR_ADDR", sReturn[1]);
		dsPT_R_ASSI_TRANS_STATEMENT.SetColumn(dsPT_R_ASSI_TRANS_STATEMENT.row, "ADDR_DETAIL_ADDR", sReturn[2]);
		//tbMain.tbInfo.ADDR_DETAIL_ADDR.Text = "";
		tbMain.tbInfo.ADDR_DETAIL_ADDR.SetFocus();
	}

	if (obj ==  tbMain.tbEtc.btnGetPost)		// 임직원 우편번호
	{
		var sReturn = fnCreateDialog("bas::PostStD.xml");
		tbMain.tbEtc.ORIGIN.value = sReturn[3];
	}

    if (obj == this.tbMain.tbEtc.btnAddr2)
	 {
		 var sReturn = fnCreateDialog("bas::PostStD.xml");
		 dsPT_R_REFFER.SetColumn(dsPT_R_REFFER.row, "ADDR", rtrim(sReturn[3]));
	 }
	 
	if (obj == this.tbMain.tbEtc.btnGetCode)	// 결격사유 
	{
		var sArgs = "_sOldCode=REGCANCEL";
		var sReturn = fnCreateDialog("bas::CommonCodeD.xml", sArgs);
		
		if (sReturn == null || sReturn.length() == 0) return;
		
		dsPT_R_REFUSAL_REASON.SetColumn(dsPT_R_REFUSAL_REASON.row, "REFS_ITEM", sReturn[0]);
		dsPT_R_REFUSAL_REASON.SetColumn(dsPT_R_REFUSAL_REASON.row, "REFUSAL_DIV_NAME", sReturn[1]);
	}
	
	if (obj == this.tbMain.tbEtc.btnGetCodeBuild) // 건물 등기부 등본 건물
	{
		var sArgs = "_sOldCode=BLDDIV";
		var sReturn = fnCreateDialog("bas::CommonCodeD.xml", sArgs);
		
		if (sReturn == null || sReturn.length() == 0) return;
		
		
		this.tbMain.tbEtc.OFFICE_USE_NAME.value = sReturn[1];
		dsPT_R_ASSI_TRANS_STATEMENT.SetColumn(dsPT_R_ASSI_TRANS_STATEMENT.row, "OFFICE_USE_CODE", sReturn[0]);
	}
	
}

