function fnSetMasterDetail(dsMaster, dsDetail, rowcnt)
{
	var sDColName;
	var sMColName;
	var sValue ="";
	
	var nMCount = object(dsMaster).colcount;
	var nDCount = object(dsDetail).colcount;
	
	if(object(dsDetail).rowcount == 0)
		object(dsDetail).addrow();
	
	for(var nLoop = 0; nLoop < nDCount; nLoop++)
	{
		sDColName = object(dsDetail).GetColID(nLoop);
		for(var nIndex = 0; nIndex < nMCount; nIndex++)
		{
			sMColName = object(dsMaster).GetColID(nIndex);
			if(trim(sDColName) == trim(sMColName))
			{
				sValue = object(dsMaster).GetColumn(0, sMColName);
				if (sValue == "null"){	sValue = ""; }
				object(dsDetail).SetColumn(rowcnt, sDColName, sValue); // 수정 함

				break;
			}
		}
	}
}


function fnSetMasterDetailMulti(dsMaster, dsDetail, rowcnt)
{

	var sDColName;
	var sMColName;
	var sValue;
	
	var nMCount = object(dsMaster).colcount;
	var nDCount = object(dsDetail).colcount;
	
	//if(object(dsDetail).rowcount == 0)
	object(dsDetail).addrow();
	
	
	for(var nLoop = 0; nLoop < nDCount; nLoop++)
	{
		sDColName = object(dsDetail).GetColID(nLoop);
		for(var nIndex = 0; nIndex < nMCount; nIndex++)
		{
			sMColName = object(dsMaster).GetColID(nIndex);
			if(trim(sDColName) == trim(sMColName))
			{
				sValue = object(dsMaster).GetColumn(rowcnt, sMColName); // 수정함
				if (sValue == "null"){	sValue = ""; }					// 수정함
				object(dsDetail).SetColumn(rowcnt, sDColName, sValue); // 수정 함
				break;
			}
		}
	}
}

