function fnSetMasterDetail_basic(dsMaster, dsDetail)
{
	var sDColName;
	var sMColName;
	var sValue;
	
	var nMCount = object(dsMaster).colcount;
	var nDCount = object(dsDetail).colcount;
	
	if(object(dsDetail).rowcount == 0)
		object(dsDetail).addrow();
	
	
	for(var nLoop = 0; nLoop < nDCount; nLoop++)
	{
		sDColName = object(dsDetail).GetColID(nLoop);
		for(var nIndex = 0; nIndex < nMCount; nIndex++)
		{
			sMColName = object(dsMaster).GetColID(nIndex);
			if(trim(sDColName) == trim(sMColName))
			{
				sValue = object(dsMaster).GetColumn(0, sMColName);
				if (sValue == "null"){	sValue = ""; }
				object(dsDetail).SetColumn(0, sDColName, sValue); 
				break;
			}
		}
	}
}

function tbMain_tbEtc_btnReferCheck_OnClick(obj)
{
	if(fnEmpty(tbMain.tbEtc.NM_KOR.Text) || fnEmpty(tbMain.tbEtc.SSN1.Text) || 
		fnEmpty(tbMain.tbEtc.SSN2.Text))
	{
		alert('이름과 주민등록번호를 입력하세요.');
		return;
	}	
	
	dsReferCheck.DeleteAll();

	var sArgs = "workMODE=REFER_CHECK scSSN1=" + quote(tbMain.tbEtc.SSN1.Text); 
		sArgs += " scSSN2=" + quote(tbMain.tbEtc.SSN2.Text);
		sArgs += " scCOM_NUM=" + dsPT_R_COMPANY_MASTER.GetColumn(0, "COM_NUM");
	
	var sTid = "REFER_CHECK";
	var sUrl = "ICCS::cm/CmAction.do?cmd=TransMgrCN";
	var sInDs = "";
	var sOutDs = "dsReferCheck=output";
	var sCallBack = "fn_CallBack";
	
	Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
	
}

