function fnDetailButton_Click(obj)
{
	var sSSN1 = tbMain.tb_Engineer.ENGINEER_SSN1.Text;
	var sSSN2 = tbMain.tb_Engineer.ENGINEER_SSN2.Text;
	var sENGINEER_NM = tbMain.tb_Engineer.ENGINEER_NM.Text;
	
	if ((fnEmpty(sSSN1) || fnEmpty(sSSN2)) && fnEmpty(sENGINEER_NM))
	{
		alert('기술자 주민등록번호 또는 기술자 이름을 입력하세요.');
		return;
	}
	
	var sArgs = "_sOldCode=" + quote(sSSN1 + sSSN2) + " _sNm=" + quote(sENGINEER_NM);
	var bRet = fnCreateDialog("st::EngListMgrD.xml", sArgs);

	
	if (bRet != null)
	{
		dsPT_R_ENGINEER_CHANGE_TEMP.SetColumn(dsPT_R_ENGINEER_CHANGE_TEMP.row, "ENGINEER_NM", trim(bRet[0]));
		dsPT_R_ENGINEER_CHANGE_TEMP.SetColumn(dsPT_R_ENGINEER_CHANGE_TEMP.row, "ENGINEER_SSN1", trim(bRet[1]));
		dsPT_R_ENGINEER_CHANGE_TEMP.SetColumn(dsPT_R_ENGINEER_CHANGE_TEMP.row, "ENGINEER_SSN2", trim(bRet[2]));
		dsPT_R_ENGINEER_CHANGE_TEMP.SetColumn(dsPT_R_ENGINEER_CHANGE_TEMP.row, "CARE_BOOK_ISSUE_NUM", bRet[3]);
		dsPT_R_ENGINEER_CHANGE_TEMP.SetColumn(dsPT_R_ENGINEER_CHANGE_TEMP.row, "TMP_FIELD", bRet[4]);
		dsPT_R_ENGINEER_CHANGE_TEMP.SetColumn(dsPT_R_ENGINEER_CHANGE_TEMP.row, "CARE_BOOK_VAL_START_DT", bRet[5]);
		dsPT_R_ENGINEER_CHANGE_TEMP.SetColumn(dsPT_R_ENGINEER_CHANGE_TEMP.row, "CARE_BOOK_VAL_END_DT", bRet[6]);
		dsPT_R_ENGINEER_CHANGE_TEMP.SetColumn(dsPT_R_ENGINEER_CHANGE_TEMP.row, "TMPSSN", bRet[1] + bRet[2]);
		dsPT_R_ENGINEER_CHANGE_TEMP.SetColumn(dsPT_R_ENGINEER_CHANGE_TEMP.row, "ENGINEER_GRADE", bRet[7]);
	}
}

