	//alert("a_tmp_num,t_tmp_num"+a_tmp_num+t_tmp_num);
				
		dsREFER_DUPLICATE.DeleteAll();
		var sArgs = "workMODE=REFER_DUPLICATE_CHECK2 scTMP_WRT_NUM="+ quote(_sKEY1);
		sArgs += " scSSN=" + quote(sSSN1 + sSSN2);
		sArgs += " ASSI_TMP_WRT_NUM="+ a_tmp_num;
		sArgs += " TRAN_TMP_WRT_NUM="+ t_tmp_num;
		var sTid = "REFER_DUPLICATE_CHECK";
		var sUrl = "ICCS::cm/CmAction.do?cmd=TransMgrCN";
		var sInDs = "dsPT_R_REFFER=dsPT_R_REFFER";
		var sOutDs = "dsREFER_DUPLICATE=output";
		var sCallBack = "fn_CallBack";
		
		Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
		http.Sync = false;
		
		if( dsREFER_DUPLICATE != null && dsREFER_DUPLICATE.rowcount > 0 ) {
			alert( dsREFER_DUPLICATE.GetColumn(0, "NM_KOR") + " 은(는) " + dsREFER_DUPLICATE.GetColumn(0, "NAME") + " 업체에 기술자로 재직 중입니다.\n" +
			 "\n대표자, 기술자를 확인하시기 바랍니다.\n데이터를 저장하지 않았습니다.");
			 
			tbMain.tb_Engineer.ENGINEER_SSN1.Value = "";
			tbMain.tb_Engineer.ENGINEER_SSN2.Value = "";
			tbMain.tb_Engineer.Edit0.SetFocus();
			return;
		}
		//-- 종료 :: 대표자가 타업체 재직기술자인지 체크2		
			
	if ((fnEmpty(sSSN1) || fnEmpty(sSSN2)) && fnEmpty(sENGINEER_NM))
	{
		alert('기술자 주민등록번호 또는 기술자 이름을 입력하세요.');
		return;
	}
	var sArgs = "workMODE=ENGINEER_CHECK scSSN=" +  quote(sSSN1 + sSSN2) +  " scNAME=" + quote(sENGINEER_NM);
	var sTid = "EngListIns";
	var sUrl = "ICCS::cm/CmAction.do?cmd=EngListMgr";
	var sInDs = "";
	var sOutDs = "dsPT_R_ENGINEER_MASTER=output";
	var sCallBack = "fn_CallBack";
	
	Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
}

