function MOT_STE_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if( MOT_STE.Value = "STD003" ) {
		MOT_STE.Enable = true;
		TMP_WRT_DT.Enable = true;
		if( fnEmpty(dsPT_R_TRANS_COMPANY_BEFORE.GetColumn(0, "CHG_DT")) ) {
			TMP_WRT_DT.Value = Today();
		}
	}
	else {
		MOT_STE.Enable = true;
		TMP_WRT_DT.Enable = true;
		TMP_WRT_DT.Value = "";
	}
}


/**
*    처리상태 값을 체크 하여 처리 일자  자동 입력    
*/
function fnCheckMotSte() {

	//양도양수 처리완료시에 진행
	if( tbMain.tbAC.MOT_STE.Value == "STD003" ) {
		tbMain.tbAC.MOT_STE.Enable = true;
		TMP_WRT_DT.Enable = true;

		/** 
		*  양도양수 처리만 신규 등록일로 처리하며
		*  법인전환, 분할 합병은 양도자의 등록일을 유지 한다
		*/
		
		if (tbMain.tbAC.ASSI_STE.Value == "1"){
		
			TMP_WRT_DT.value = Today();
		} else {		
			if( !fnEmpty(dsPT_R_TRANS_COMPANY_BEFORE.GetColumn(0, "WRT_DT")) ){
			
				TMP_WRT_DT.Value = dsPT_R_TRANS_COMPANY_BEFORE.GetColumn(0, "WRT_DT");
			} else {
			
				//TMP_WRT_DT.Value = Today();
			}
		}
		
	} else {
		tbMain.tbAC.MOT_STE.Enable = true;
		TMP_WRT_DT.Enable = true;
		TMP_WRT_DT.Value = "";
	}
}

