    }
}

// 변경사항(ABCD) 체크시 해당 컨트롤들의 Enable 변경  
function chkExtChg_OnClick(obj,strValue)
{
	// A,B,C,D 중 하나
	var extChg = right(obj.ID, 1);
	
	if( extChg == "A" ) {
		tbMain.tbInfo.ADDR_ADDR.Enable = strValue;
		tbMain.tbInfo.ADDR_DETAIL_ADDR.Enable = strValue;
		tbMain.tbInfo.ADDR_POST_NUM.Enable = strValue;
		tbMain.tbInfo.btnGetPost.Enable = strValue;
	} // if( extChg == "A" )
	
	else if( extChg == "B" ) {
		fnAllControlState(strValue, tbMain.tbInfo);
	} // if( extChg == "B" )
	
	else if( extChg == "C" ) {
		tbMain.tbInfo.REP_NAME_KOR.Enable = strValue;
		tbMain.tbInfo.Edit1.Enable = strValue;
	} // if( extChg == "C" )
	
	else if( extChg == "D" ) {
		tbMain.tbInfo.NAME.Enable = strValue;
		tbMain.tbInfo.btnG4C.Enable = strValue;
	} // if( extChg == "D" )

}

//////////////////////////////////////////////////////////////////
// 결격사유 관련 처리
//////////////////////////////////////////////////////////////////
function fnRefusalDetailButton_Click(obj)
{
	var sDtWorkFlag;
	var sCHGBRE_SEQ = "1";
	// 등록된 공사업체 
	if (tbMain.tbAC.rdoAssi.index == 1 && tbMain.tbAC.rdoTran.index == 0){
		sCHGBRE_SEQ = this.dsPT_R_ASSI_TRANS_STATEMENT.GetColumn(0, "TRAN_CHGBRE_SEQ");
	} else if (tbMain.tbAC.rdoAssi.index == 1 && tbMain.tbAC.rdoTran.index == 1){
		alert("양도양수탭에서 등록업체를 선택해 주세요");
		return;
	} else {
		sCHGBRE_SEQ = this.dsPT_R_ASSI_TRANS_STATEMENT.GetColumn(0, "ASSI_CHGBRE_SEQ");
	}
	
	for (var i = 0; i <= dsPT_R_REFUSAL_REASON.row; i++){
			dsPT_R_REFUSAL_REASON.SetColumn(i, "CHGBRE_SEQ", sCHGBRE_SEQ);
	}
		
	if(obj == tbMain.tbEtc.btnRefusalInsert)
	{
	
	 var _sKEY1 = dsPT_R_ASSI_TRANS_STATEMENT.GetColumn(0, "ASSI_TMP_WRT_NUM");

		sDtWorkFlag = global.gv_INSERT;
		dsPT_R_REFUSAL_REASON.AddRow();
		dsPT_R_REFUSAL_REASON.SetColumn(dsPT_R_REFUSAL_REASON.row, "TMP_WRT_NUM", _sKEY1);
		dsPT_R_REFUSAL_REASON.SetColumn(dsPT_R_REFUSAL_REASON.row, "SEQ", dsPT_R_REFUSAL_REASON.rowcount);
											
		fnInitRefusalGroup(sDtWorkFlag);
		
	}
	else if (obj == tbMain.tbEtc.btnRefusalModify)
	{
	
		sDtWorkFlag = global.gv_MODIFY;
		fnInitRefusalGroup(sDtWorkFlag);
		
	}
	else if (obj == tbMain.tbEtc.btnRefusalRemove)
	{
	
		sDtWorkFlag = global.gv_INQUIRY;
		
		dsPT_R_REFUSAL_REASON.ApplyChange();
		var nCount = fnGridRowRemove(dsPT_R_REFUSAL_REASON);

		if(nCount == 0)
		{
			alert('삭제할 데이터를 선택하세요.');
			return;
		}
		
		var sArgs = "workMODE=REFUSAL";	
		var sTid = "D_REFUSAL";
		var sUrl = "ICCS::cm/CmAction.do?cmd=TransMgrCCUDN";
		var sInDs = "dsPT_R_REFUSAL_REASON=dsPT_R_REFUSAL_REASON:u";
		var sOutDs = "dsPT_R_REFUSAL_REASON=output";
		var sCallBack = "fn_CallBack";

		Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
		
	}
	else if (obj == tbMain.tbEtc.btnRefusalCancel)
	{
	
		if (dsPT_R_REFUSAL_REASON.GetRowType(dsPT_R_REFUSAL_REASON.row) == "insert")
			dsPT_R_REFUSAL_REASON.DeleteRow(dsPT_R_REFUSAL_REASON.row);
		else
			fnRejectChange(dsPT_R_REFUSAL_REASON);
		
		sDtWorkFlag = global.gv_INQUIRY;
		fnInitRefusalGroup(sDtWorkFlag);
				
	}
	else if (obj == tbMain.tbEtc.btnRefusalSave)
	{
	
		var sArgs = "workMODE=REFUSAL";
		var sTid = "I_REFUSAL";
		var sUrl = "ICCS::cm/CmAction.do?cmd=TransMgrCCUDN";
		var sInDs = "dsPT_R_REFUSAL_REASON=dsPT_R_REFUSAL_REASON:u";
		var sOutDs = "dsPT_R_REFUSAL_REASON=output";
		var sCallBack = "fn_CallBack";
		
		Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
		
	}
}


/**
 1. 함수명 : fnCheckReceiptNum
 2. 기  능 : 접수번호 생성
 3. Params 
    없음
 4. Return
    없음
*/
function fnRECV_NUM_Chk(obj)
{
	if(global.gv_WorkFlag == global.gv_INSERT)
	{
		if(fnEmpty(this.RECV_NUM.Text))
		{
			alert('접수번호를 입력하세요.');
			return;
		}
		
		dsCheckKey.DeleteAll();
		
		var sArgs = "scRECV_NUM=" + this.RECV_NUM.Text + " workMODE=RECV_NUM_CHECK";
			sArgs += " scSIDO_CODE=" + quote(global.g_Sd_cd);
		var sTid = "RECV_NUM_CHECK_KEY";
		var sUrl = "ICCS::cm/CmAction.do?cmd=TransMgrCN";
		var sInDs = "";
		var sOutDs = "dsCheckKey=output dsCheckKey2=output2 dsCheckKey3=output3 dsCheckKey4=output4";
		var sCallBack = "fn_CallBack";
		
		Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
	}	
}

