{	
	//if (tbMain.tbAC.ASSI_STE.Value == "3"){
	ASSI_STE = dsPT_R_ASSI_TRANS_STATEMENT.getColumn(0,"ASSI_STE");

    if(ASSI_STE == "3"){
		tbMain.tbAC.rdoTran.Visible = "true";
		tbMain.tbAC.Static20.Visible = "true";
		tbMain.tbAC.Image18.Visible = "true";		
	} else {
		tbMain.tbAC.rdoTran.Visible = "false";
		//tbMain.tbAC.TRAN_COMNUM.text = "";
		//tbMain.tbAC.TRAN_COMNUM.value = "";
		tbMain.tbAC.Static20.Visible = "false";
		tbMain.tbAC.Image18.Visible = "false";		
	}
}

function ASSI_STE_LOAD(obj,strCode,strText,nOldIndex,nNewIndex)
{	

    ASSI_STE = dsPT_R_ASSI_TRANS_STATEMENT.getColumn(0,"ASSI_STE");

    if(ASSI_STE == "3")
    {
    
    tbMain.tbAC.rdoTran.Visible = "true";
	tbMain.tbAC.Static20.Visible = "true";
	tbMain.tbAC.Image18.Visible = "true";
	}
	else
	{
	
	tbMain.tbAC.rdoTran.Visible = "false";
	tbMain.tbAC.Static20.Visible = "false";
	tbMain.tbAC.Image18.Visible = "false";
	}

}	

		
