		tb_IF.ADDR_ADDR.value = tb_AC.TRAN_ADDR.value;				//주소
		tb_IF.ADDR_DETAIL_ADDR.value = tb_AC.TRAN_DETAIL_ADDR.value;//주소

	}
}

function tbMain_OnChanged(obj,nOldIndex,nNewindex)
{
	var tb_AC = tbMain.tbAC;
	var tb_IF = tbMain.tbInfo;
	var tb_Engineer = tbMain.tb_Engineer;
	
	//nNewindex :0 (양도양수) , :1 (기본정보), :2 (보유기술자),  :3 부가정보
	
	if (tb_AC.rdoTran.value == "" && tb_AC.rdoAssi.value == "")
	{	
		if (nNewindex <> 0 ){
			alert("양도양수 분류를 선택하여 주십시오");
			tbMain.TabIndex = 0;	
		}
	}

	if (tb_AC.ASSI_COI_WRT_NUM.value == "" && tb_AC.TRAN_COI_WRT_NUM.value == "")
	{
		if (nNewindex <> 0 ){
			alert("공사업체 입력을 해 주십시오");
			tbMain.TabIndex = 0;	
		}
	}
	
	if (nNewindex == 1)
	{

		// - 양수자
		tb_IF.NAME.value = tb_AC.TRAN_NAME.value;					// 공사업체 (상호)
		tb_IF.MANA_NUM.value = tb_AC.TRAN_MANA_NUM.value; 			// 사업자 등록번호
		tb_IF.COM_NUM.value = tb_AC.TRAN_COMNUM.value; 			    // 법인번호
		tb_IF.REP_NAME_KOR.value = tb_AC.TRAN_REP.value; 			// 대표자
		tb_IF.ADDR_TEL_NUM.value = tb_AC.TRAN_TELNUM.value; 		// 연락처
			
		tb_IF.ADDR_POST_NUM.value = tb_AC.TRAN_POST_NUM.value;		//주소
		tb_IF.ADDR_ADDR.value = tb_AC.TRAN_ADDR.value;				//주소
		tb_IF.ADDR_DETAIL_ADDR.value = tb_AC.TRAN_DETAIL_ADDR.value;//주소
		//tb_IF.dsRECV_DIV.Value = "1";
		//tb_IF.Radio1.Value = "N";
		
		//공사등록업체 우선으로 등록번호를 지정
		if (tb_AC.rdoTran.Index == 1){
			// 법인 처리
			//tb_IF.COMMANA_CLASS.index = dsPT_R_ASSI_TRANS_STATEMENT.GetColumn(0,"ASSI_COMMANA_CLASS"); 
			// 법인 구분 
			//dsPT_R_ASSI_TRANS_STATEMENT.SetColumn(0,"COMMANA_CLASS","3");
			// 양도자 등록번호
			dsPT_R_ASSI_TRANS_STATEMENT.SetColumn(0,"COI_WRT_NUM",tb_AC.ASSI_COI_WRT_NUM.value);

		} else if (tb_AC.rdoAssi.Index == 0 && tb_AC.rdoTran.Index == 0 ){
			// 양수자 등록번호
			//dsPT_R_ASSI_TRANS_STATEMENT.SetColumn(0,"COI_WRT_NUM",tb_AC.ASSI_COI_WRT_NUM.value);
			dsPT_R_ASSI_TRANS_STATEMENT.SetColumn(0,"COI_WRT_NUM",tb_AC.TRAN_COI_WRT_NUM.value);
			
		} else if (tb_AC.rdoAssi.Index == 0 && tb_AC.rdoTran.Index == 1 ){
			// 양도자 등록번호가 없을시에 양수자 등록번호
			if (tb_AC.TRAN_COI_WRT_NUM.value <> ""){
				dsPT_R_ASSI_TRANS_STATEMENT.SetColumn(0,"COI_WRT_NUM",tb_AC.TRAN_COI_WRT_NUM.value);
			} else {
				 tb_AC.rdoTran.Index = 0;
				 alert("공사업체 조회를 해 주십시오");
			}
			
		}
		
		// 업무구분 -> 법인 전환 처리시
		if (tbMain.tbInfo.COMMANA_CLASS.value == 1){
				//tb_IF.COM_NUM.value = tb_AC.TRAN_COMNUM.value;
				//dsPT_R_ASSI_TRANS_STATEMENT.SetColumn(0,"COMMANA_CLASS",3);	// 법인 구분
		} else {
				//tb_AC.TRAN_COMNUM.value= "";
				//dsPT_R_ASSI_TRANS_STATEMENT.SetColumn(0,"COMMANA_CLASS",0);
		}
		
	} else if (nNewindex == 2){

		/*var assi_tmp_wrt_num = 	dsPT_R_ASSI_TRANS_STATEMENT.GetColumn(0,"ASSI_TMP_WRT_NUM");
		var tran_tmp_wrt_num = 	dsPT_R_ASSI_TRANS_STATEMENT.GetColumn(0,"TRAN_TMP_WRT_NUM");
		
		var sArgs = "workMODE=Engineer_List scAS_TMP_WRT_NUM="+assi_tmp_wrt_num+" scTR_TMP_WRT_NUM=" + tran_tmp_wrt_num;
		var sTid = "Engineer_List";		
		var sOutDs = "dsENGINEER_CHANGE_TEMP=output ";		
		var sUrl = "ICCS::cm/CmAction.do?cmd=TransMgrC";		
		var sCallBack = "fn_CallBack";		
		*/
	}
}


function tbMain_tbInfo_COMMANA_CLASS_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
		
	 if (dsPT_R_ASSI_TRANS_STATEMENT.GetColumn(dsPT_R_ASSI_TRANS_STATEMENT.row, "COMMANA_CLASS") != "1")
	{
		fnControlState(false, tbMain.tbInfo, "PAY_CAP");
	}
	else{
    	fnControlState(true, tbMain.tbInfo, "PAY_CAP");
	}
	
		if (dsPT_R_ASSI_TRANS_STATEMENT.GetColumn(dsPT_R_ASSI_TRANS_STATEMENT.row, "COMMANA_CLASS") = "2")
	{
		fnControlState(false, tbMain.tbInfo, "COM_NUM");
		tbMain.tbInfo.COM_NUM.Text = "";
	}
	else{
	    fnControlState(true, tbMain.tbInfo, "COM_NUM");
	}
}


function ds_Clear()
{
		dsPT_R_SUBSIDIARY.DeleteAll();
		dsPT_R_REFUSAL_REASON.DeleteAll();
		dsPT_R_REFFER.DeleteAll();
		dsPT_R_MOT_STE.DeleteAll();
		dsPT_R_ENGINEER_CHANGE_TEMP.DeleteAll();
}


function fnLocalControlState()
{	
	if (tbMain.tbInfo.DIAGNOSTIC_DIV.Value == "DIM002")
	{
		tbMain.tbInfo.stDiag.Value = "재무제표년도";
		//tbMain.tbInfo.comDIAG.Visible = true;
		tbMain.tbInfo.COMPANY_DIAG_BAS_DT.Visible =false;
		
		if (dsPT_R_ASSI_TRANS_STATEMENT.GetColumn(dsPT_R_ASSI_TRANS_STATEMENT.row, "COMMANA_CLASS") != "1")
			fnControlState(false, tbMain.tbInfo, "PAY_CAP");
		else
			fnControlState(true, tbMain.tbInfo, "PAY_CAP");
		
		if (dsPT_R_ASSI_TRANS_STATEMENT.GetColumn(dsPT_R_ASSI_TRANS_STATEMENT.row, "COMPANY_DIAG_CLASS_CODE") == "DIM002")
		{
			//tbMain.tbInfo.PAY_CAP.value = "";
			fnControlState(false, tbMain.tbInfo, "PAY_CAP");
		} else {
			fnControlState(true, tbMain.tbInfo, "PAY_CAP");
		}
						
	} else {	
	
		tbMain.tbInfo.stDiag.Value = "진 단 기 준 일";
		//tbMain.tbInfo.comDIAG.Visible = false;
		tbMain.tbInfo.COMPANY_DIAG_BAS_DT.Visible = true;
		fnControlState(true, tbMain.tbInfo, "PAY_CAP");
	}
	
	fnCheckMotSte();
	
}

/**
* 	재무재표 년도
*/
function fnSetYear()
{
	var nIndex = 0;
	for (var nLoop = 1990; nLoop < 2050; nLoop++)
	{
		dsDIAG.AddRow();
		dsDIAG.SetColumn(nIndex, "Code", nLoop);
		dsDIAG.SetColumn(nIndex, "Value", nLoop);
		
		nIndex++;
	}
	
	var strDate = GetDate();
	strDate = Mid(strDate, 0, 4);
	
	//tbMain.tbInfo.comDIAG.Value = strDate;
}

/*
*	등록완료시 제어 
*	등록완료시에 수정시 문제 발생 
*   원인 : 
*		1. 실적, 시공능력 데이터는 등록된 공사업체들의 실적,시공능력 데이터 증가 처리를 위해
*   기 데이터를 지우고 추가를 하므로 실적, 시공능력 데이터 값이 잘못 될수 있음.
*		2. 협회연동처리를 통해 협회쪽으로  데이터가 전송되므로 또한 수정 불가
*/
function fnComplete(){
	if (this.MOT_STE.value == "STD003"){
		fnAllControlState(false, this);
		this.ProcessCheck.enable = false;
		//divButtonBar.btnReport.enable = true;
		divButtonBar.btnList.enable = true;

	}
}


function fninitRECV_DIV(){
	if (this.tbMain.tbInfo.GUBUN.Value == ""  || this.tbMain.tbInfo.GUBUN.Value == " " ){		
		if (g_RECV_DIV == "본사" || g_RECV_DIV == ""){
		
			this.tbMain.tbInfo.GUBUN.value = "본사";
			this.tbMain.tbInfo.dsRECV_DIV.Index = 0;
		} else if (g_RECV_DIV == "제2청사") {
			this.tbMain.tbInfo.GUBUN.value = "제2청사";
			this.tbMain.tbInfo.dsRECV_DIV.Index = 1;
		}
	}			
}
function tbMain_tbAC_TRAN_REP_OnKillFocus(obj)
{
		var tb_AC = tbMain.tbAC;
     	var tb_IF = tbMain.tbInfo;
		// - 양수자
		tb_IF.NAME.value = tb_AC.TRAN_NAME.value;					// 공사업체 (상호)
		tb_IF.MANA_NUM.value = tb_AC.TRAN_MANA_NUM.value; 			// 사업자 등록번호
		tb_IF.COM_NUM.value = tb_AC.TRAN_COMNUM.value; 			// 법인번호
		tb_IF.REP_NAME_KOR.value = tb_AC.TRAN_REP.value; 			// 대표자
		tb_IF.ADDR_TEL_NUM.value = tb_AC.TRAN_TELNUM.value; 		// 연락처
			
		tb_IF.ADDR_POST_NUM.value = tb_AC.TRAN_POST_NUM.value;		//주소
		tb_IF.ADDR_ADDR.value = tb_AC.TRAN_ADDR.value;				//주소
		tb_IF.ADDR_DETAIL_ADDR.value = tb_AC.TRAN_DETAIL_ADDR.value;//주소

}


function fnDOC_Click(obj)
{	     
		dsCheckKey.DeleteAll();
		
		var sArgs = "DOC_CODE=" +  quote(Edit0.Text) + " workMODE=DOC_CODE_CHECK";
			sArgs += " scSIDO_CODE=" + quote(global.g_Sd_cd);
		var sTid = "DOC_CODE_CHECK_KEY";
		var sUrl = "ICCS::cm/CmAction.do?cmd=TransMgrCN";
		var sInDs = "";
		var sOutDs = "dsCheckKey=output dsCheckKey2=output2 dsCheckKey3=output3 dsCheckKey4=output4 dsCheckKey5=output5 dsCheckKey6=output6";
		var sCallBack = "fn_CallBack";
		
		Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
		
}

