Window.Script:
var pg_code;
var next_code = 0;
var title;

function MainLeft_OnLoadCompleted(obj)
{	

	fn_subHide();
	
	//권한별 리스트를 가지고 온다.
	var sUrl = global.g_ext_iccs_url + "menu/manu.jsp?scUSER_ID="+g_User_ID+"&scCAPITAL="+g_Capital;
	http.TimeOut=100; //로딩 시간 설정(클라이언트 로딩 시간이 길 경우 값을 주면 됨 
					  // 서버쪽에서는 설정 할 필요 없음
	LoadMenu(sUrl);	
	
	//현재시간이랑 날짜를 가지고 온다.
	var curTime = getDate() + (Timem() - toFloat(Time()+"000"));
	s_data.Text = ToString(curTime,"@@@@.@@.@@ @@:@@");

	//사용자 명을 삽입 한다.  
	this.userName.Text = g_Name;        
	this.s_name = g_SIDO_FULL_NM;
	
	if(next_code == 0){
		
		next_code = 1;
		var arg = "scUSER_ID="+g_User_ID+"&scPASSWD="+g_passwd;
		Microsoft0.Navigate(global.g_ext_iccs_url + "login/LoginAction.do?cmd=LoginLife&"+arg,"","","","");
		
		if(global.g_Capital == "B" || global.g_Capital == "M" || global.g_Capital == "D")
		{	global.MainMenu.Visible = false;
			gfnCreateNewWindow("main::Main_Sido", "./Main_Sido.xml", "");
		}else{
			global.MainMenu.Visible = false;
			gfnCreateNewWindow("main::Main_Sigungu", "./Main_Sigungu.xml", "");
		}
	}

}
function fn_Explorer(){
	
}

function div_btnmenu_OnClick(obj,srtID)
{	
	if(obj.userdata != "")
	{
		lfn_setMenu(obj.id);
		
		if(obj.userdata != ""){
			lift_fn_newWindow(pg_code, obj.userdata);
			var arg = "scUSER_ID="+g_User_ID+"&scPASSWD="+g_passwd;
			Microsoft0.Navigate(global.g_ext_iccs_url + "login/LoginAction.do?cmd=LoginLife&"+arg,"","","","");
		}
	}
}


function lfn_setMenu(arg)
{
	global.MainMenu.Visible = true;
	global.MainMenu.Image2.text = g_Titel;
	for(i=1; i < gds_menulist.RowCount() ; i++){
		obj = object("global.MainMenu.sub_img"+(i));		
		if(obj.id == arg){
			obj.ImageID = "left_menu2_"+(i)+"_on";
			pg_code = gds_menulist.GetColumn(i, "frmid");
			g_exurl = gds_menulist.GetColumn(i, "frmurl");
			var txt = gds_menulist.GetColumn(i,"frmtxt");
			title = txt;
			obj.Color = "#FFFFFF";
		}else{
			obj.ImageID = "left_menu2_"+(i);
			obj.Color = "#1c3c5a";
		}	
	}
	
}

function lift_fn_newWindow(frmNM, frmURL) 
{

	if (trim(length(frmURL)) < 1) 
	{
		return;
	}
	
	var objWin = AllWindows(frmNM);
	var strArg = "titles="+title; 
	g_exurl = "";
	
	for (var nLoop = 0; nLoop < global.AllWindows.count; nLoop++)
	{
		if (global.AllWindows[nLoop].Id == "MainMenu")
			continue;
		//else if (global.AllWindows[nLoop].Id == "main::Main_Sido" 
		//	|| global.AllWindows[nLoop].Id == "main::Main_Sigungu")
		//{
		//	continue;
		//}
		else
			global.AllWindows[nLoop].Close();
	}
	
	if(IndexOf(frmURL,"cmd") > 0)
	{
		g_exurl = frmURL;
		NewWindow(frmNM, "bas::Explorer.xml", strArg, "", "", "OpenStyle=MAX", -1, -1);
	}
	else
	{		
		NewWindow(frmNM, frmURL, strArg, "", "", "OpenStyle=MAX", -1, -1);
	}
}

