function fn_Home_Page(obj,nX,nY)
{
	var go_url = "http://www.net.go.kr/login/LoginAction.do?cmd=Login&id="+g_User_ID+"&password="+g_passwd+"&encoding=Y&beforeURL=/";
	
	ExecBrowser( go_url);
	
}

function Image1_OnClick(obj,nX,nY)
{
	if(global.g_Capital == "B" || global.g_Capital == "M" || global.g_Capital == "D")
	{	global.MainMenu.Visible = false;
		gfnCreateNewWindow("main::Main_Sido", "../main/Main_Sido.xml", "");
	}
	else
	{
		global.MainMenu.Visible = false;
		gfnCreateNewWindow("main::Main_Sigungu", "../main/Main_Sigungu.xml", "");
	}	
}

function fnMouseOver(obj,nX,nY)
{
	obj.Cursor = "HAND";
}
