/**
 1. 함수명 : fnRemove
 2. 기  능 : 삭제버튼 이벤트 핸들러
 3. Params 
    없음
 4. Return
    없음
*/
function fnRemove()
{
	if (ds_PT_MI_USER.FindRow("CHECKER", "1") < 0)
	{
		alert('삭제할 데이터를 선택하세요.');
		return;
	}
	
	if(!Confirm('선택하신 내용을 삭제하시겠습니까?')) return;
	
	ds_PT_MI_USER.ApplyChange();
	var nCount = fnGridRowRemove(ds_PT_MI_USER);
	
	if(nCount == 0)
	{
		alert('데이터 삭제 중 오류가 발생하였습니다.');
		return;
	}
	
	var sArgs = "";	
	var sTid = "DELETE";
	var sUrl = "ICCS::sys_m/Sys_mAction.do?cmd=UserMgrCUD";
	var sInDs = "ds_PT_MI_USER=ds_PT_MI_USER:U";
	var sOutDs = "ds_PT_MI_USER=output";
	var sCallBack = "fn_CallBack";
		
	Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
}

/**
 1. 함수명 : fnSave
 2. 기  능 : 저장버튼 이벤트 핸들러
 3. Params 
    없음
 4. Return
    없음
*/
function fnSave()
{
	if(id_chk == "N")
	{
		alert('아이디 중복 체크를 하지 않았습니다.');
		return;
	}
	
	if(pw_chk == "N")
	{
		alert('비밀번호가 일치 하지 않았습니다.');
		return;
	}
	
	if(this.divText.NM == "")
	{
		alert('이름을 입력하지 않았습니다.');
		return;
	}
	
	if(this.divText.OFFPART == "")
	{
		alert('부서를 입력하지  않았습니다.');
		return;
	}
	
	if(this.divText.Edit2 == "")
	{
		alert('직급을 입력하지 않았습니다.');
		return;
	}
	
	if(this.divText.E_MAIL == "")
	{
		alert('E-mail을 입력하지 않았습니다.');
		return;
	}

	if(this.divText.SIDO_CODE.Index < 0)
	{
		alert("시도가 선택 되지 않았습니다.");
		return;
	}
	
	if(this.divText.SIGUNGU_CODE.Index < 0)
	{
		alert("시군구가 선택 되지 않았습니다.");
		return;
	}
	
	/*if(this.divText.Picture == "")
	{
		alert('사진이 첨부되지않았습니다.');
		return;
	}
	
	if(fnEmpty(dsUploadFile.GetColumn(0, "file_name")))
	{
		alert('사진이 첨부되지않았습니다.');
		return;
	}*/
	
	/*if(this.divText.TEL != "")
	{
		var TEL1= substr(this.divText.TEL.value,0,3);
		var TEL2 = substr(this.divText.TEL.value,4,3);
		var TEL3 = substr(this.divText.TEL.value,8,4);
		
		
		ds_PT_MI_USER.SetColumn(0, "TEL1",TEL1);
		ds_PT_MI_USER.SetColumn(0, "TEL2",TEL2);
		ds_PT_MI_USER.SetColumn(0, "TEL3",TEL3);
		
		
	}*/
  
   var Temp=this.divText.TEL1+"-"+this.divText.TEL2+"-"+this.divText.TEL3;
   ds_PT_MI_USER.SetColumn(ds_PT_MI_USER.row,"TEL",Temp);
  
   var Temp1=this.divText.MOBILE1+"-"+this.divText.MOBILE2+"-"+this.divText.MOBILE3;
   ds_PT_MI_USER.SetColumn(ds_PT_MI_USER.row,"MOBILE",Temp1);
	
   var Temp2=this.divText.FAX1+"-"+this.divText.FAX2+"-"+this.divText.FAX3;
   ds_PT_MI_USER.SetColumn(ds_PT_MI_USER.row,"FAX",Temp2);
	
	if((this.divText.TEL1.index<0 ) or this.divText.TEL2 == "" or this.divText.TEL3 == "")
	{
		alert('연락처를 입력하지 않았습니다.');
		return;
	}
	
	
	if((this.divText.MOBILE1.index<0 ) or this.divText.MOBILE2 == "" or this.divText.MOBILE3 == "")
	{
		alert('휴대폰번호를 입력하지 않았습니다.');
		return;
	}
	
	if((this.divText.FAX1.index<0 ) or this.divText.FAX2 == "" or this.divText.FAX3 == "")
	{
		alert('Fax번호를 입력하지 않았습니다.');
		return;
	}
    
    ds_PT_MI_USER.SetColumn(0, "WRT_ID",ds_PT_MI_USER.getColumn(0,"offi_id"));
    
	var sArgs = "workMODE=ALL";
	var sTid = "save";
	var sUrl = "ICCS::sys_m/Sys_mAction.do?cmd=UserMgrBasicCUD";
	var sInDs = "ds_PT_MI_USER=ds_PT_MI_USER:u";
	var sOutDs = "ds_PT_MI_USER=output";
	var sCallBack = "fn_CallBack";
	
	
	// 2009-10-16 회원가입시 시군구+id 입력없이 먼저 사진 등록시에 파일명이 생성이 안되므로 수정함
	var sExt = split(FileDialog0.FileName, ".");
	ds_PT_MI_USER.SetColumn(0,"PHOTO", this.divText.SIDO_CODE.Value + "_" + divText.OFFI_ID.Text + "." + sExt[length(sExt) - 1]);
	

	
	//if (sExt[length(sExt) - 1] != "jpg"){
	//	alert('사진등록시 확장자가 jpg 만 가능합니다.');
	//	return;
	//}
		
	Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);

	global.gv_WorkFlag = global.gv_INQUIRY;
	fnInitButtonBar();
}

function fnCancel()
{
	if (ds_PT_MI_USER.GetRowType(ds_PT_MI_USER.row) == "insert")
		ds_PT_MI_USER.DeleteRow(ds_PT_MI_USER.row);
	else
		fnRejectChange(ds_PT_MI_USER);
		
	global.gv_WorkFlag = global.gv_INQUIRY;
	fnInitButtonBar();
}

