function fnGetCode()
{
	var sArgs = "";
	var sTid = "GET_CODE";
	var sUrl = "ICCS::sys_m/Sys_mAction.do?cmd=SidoCode";
	var sInDs = "";
	var sOutDs = "dsSIDO_CODE=output";
	var sCallBack = "fn_CallBack";
	
	Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);	
}
