function fnModify()
{
	var sKEY1 = dsPT_R_COMPANY_MASTER.GetColumn(dsPT_R_COMPANY_MASTER.row, "COI_WRT_NUM");
    
    var sArgs = "_sURL="+global.g_ext_iccs_url+"reg/RegAction.do?cmd=RegMgrViewClose";
	//var sArgs = "_sURL=http://99.1.5.75/reg/RegAction.do?cmd=RegMgrViewClose";
	//var sArgs = "_sURL=http://localhost/iccsmWebNew/reg/RegAction.do?cmd=RegMgrViewClose";
	
		sArgs += "&coi_wrt_num=" + sKEY1;
	var sReturn = fnCreateDialog("bas::frmWebRegList.xml", sArgs);
}
