//////////////////////////////////////////////////////////////////
// Form Load 이벤트
//////////////////////////////////////////////////////////////////
function fnForm_Loaded(obj)
{
	global.gv_WorkFlag = global.gv_INQUIRY;
	
	if(global.gv_WorkFlag == global.gv_INQUIRY)
	{
		fnInquiry();
	}
	
	divButtonBar.Image1.text = space(6) + "공사업체 상세조회";
	this.RegListMgrC.Title = "공사업체 조회";
	tbMain.TabIndex = 0;
	
	if( _popup=="T" ){		
		divButtonBar.Url = "base::frmButtonBarFlat.xml";
	}
}

function fnForm_Activate(obj)
{
	global.g_activeFrm = this.Id;
}

