/**
 1. 함수명 : fnInitButtonBar
 2. 기  능 : 화면상단 버튼상태 설정
 3. Params 
    없음
 4. Return
    없음
*/
function fnInitButtonBar()
{
	if (global.gv_WorkFlag == global.gv_INQUIRY)
	{
		fnAllControlState(false, this);	
		fnControlState(true, this.tbMain.tab4, "grdList");
		fnControlState(true, this.tbMain.tpMemo, "grdList");
		fnControlState(true, this.tbMain.tpEngeneer, "grdList");
		fnControlState(true, this.tbMain.tbBUB, "grdList");

		divButtonBar.btnReport.enable = false;
		divButtonBar.btnList.enable = true;
	}
}


/**
 1. 함수명 : fnTablesBinding
 2. 기  능 : 검색조건에 대한 검색
 3. Params 
    없음
 4. Return
    없음
*/
function fnTablesBinding()
{
	var sArgs = "scTMP_WRT_NUM=" + _sKEY1;
	var sTid = "SEARCH";
	var sUrl = "ICCS::cm/CmAction.do?cmd=RegListMgrC";
	var sInDs = "";
	var sOutDs  = "dsPT_R_COMPANY_MASTER=output dsPT_R_BASIC_CHANGE_HISTORY=output2 " ;
		sOutDs += "dsPT_M_MASTER=output3 dsPT_R_WORK_CAPABILITY=output4 dsPT_R_WORK_RESULTS=output5 dsPT_R_ENGINEER=output6";

	var sCallBack = "fn_CallBack";
	
	Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
}

function fn_CallBack(Svcid, sErrCode, sErrMsg) 
{
	if (sErrCode < 0) 	
	{
		if (svcid == "SEARCH")
		{
			alert('데이터 조회 중 오류가 발생하였습니다.');
			return;
		}
	}
}

