/**
 1. 함수명 : fnInitButtonBar
 2. 기  능 : 화면상단 버튼상태 설정
 3. Params 
    없음
 4. Return
    없음
*/
function fnInitButtonBar()
{
	divButtonBar.btnList.enable = false;
	
	if (global.gv_WorkFlag == global.gv_INQUIRY)
	{
		fnAllControlState(true, this);
		
		divButtonBar.btnInsert.enable = true;
		divButtonBar.btnModify.enable = true;
		divButtonBar.btnRemove.enable = true;
		
		divButtonBar.btnSave.enable = fnSetMenuSaveButtonAuth(2, false);
		divButtonBar.btnReport.enable = false;
		divButtonBar.btnExcel.enable = true;
		divButtonBar.btnCancel.enable = false;
		
		fnAllControlState(false, divText);
	}
	else if (global.gv_WorkFlag == global.gv_INSERT)
	{
		fnAllControlState(false, this);
		
		divButtonBar.btnInsert.enable = false;
		divButtonBar.btnModify.enable = false;
		divButtonBar.btnRemove.enable = false;
		
		divButtonBar.btnSave.enable = fnSetMenuSaveButtonAuth(2, true);
		divButtonBar.btnReport.enable = false;
		divButtonBar.btnExcel.enable = false;
		divButtonBar.btnCancel.enable = true;
		
		fnAllControlState(true, divText);
		divText.SIDO_CODE.SetFocus();
	}
	else
	{
		fnAllControlState(false, this);
		
		divButtonBar.btnInsert.enable = false;
		divButtonBar.btnModify.enable = false;
		divButtonBar.btnRemove.enable = false;
		
		divButtonBar.btnSave.enable = fnSetMenuSaveButtonAuth(2, true);
		divButtonBar.btnReport.enable = false;
		divButtonBar.btnExcel.enable = false;
		divButtonBar.btnCancel.enable = true;
		
		fnAllControlState(true, divText);
		fnControlState(false, divText, "SIDO_CODE,OFFI_ID");
		divText.NM.SetFocus();
	}
}

/**
 1. 함수명 : fnTablesBinding
 2. 기  능 : 검색조건에 대한 검색
 3. Params 
    없음
 4. Return
    없음
*/
function fnTablesBinding()
{
	var sArgs = "workMODE=SEARCH";
	sArgs += " scNM=" + quote(scNM.Value);
	
	sArgs += " scSIDO=" + quote(scSIDO.Value);

    

	var sTid = "SEARCH";
	var sUrl = "ICCS::sys_m/Sys_mAction.do?cmd=UserMgrC";
	var sInDs = "";
	var sOutDs = "ds_PT_MI_USER=output";
	var sCallBack = "fn_CallBack";
	
	Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
}

function fn_CallBack(Svcid, sErrCode, sErrMsg) 
{
	if (sErrCode < 0) 	
	{
		alert(sErrCode + " : " + sErrMsg);
		if (svcid == "SEARCH")
		{
			alert('데이터 조회 중 오류가 발생하였습니다.');
			return;
		}
		
		if (svcid == "DELETE")
		{
			alert('데이터 삭제 중 오류가 발생하였습니다.');
			return;
		}
		
		if (scvid == "save")
		{
			alert('데이터 저장 중 오류가 발생하였습니다.');
			return;
		}
	}
	
	if (svcid == "DELETE")
	{
		alert('삭제되었습니다.');
		return;
	}
	
	if (svcid == "IDCHECK")
	{
		var chk = ds_IDCHK.GetColumn(0, "CHKYN");
		if(chk != "Y"){
			alert('아이디가 중복 입니다.');
			this.divText.OFFI_ID.SetFocus();
			return;
		}
	}
	
	if (svcid == "SEARCH")
	{
		scSIDO.DisplayRowCnt = ds_SIDO.rowcount;
		scSIDO.Value = global.g_Sd_cd;
		return;
	}
	
	if (svcid == "save")
	{
		alert('저장되었습니다.');
		return;
	}
}

