function fnKillFocus(obj)
{	
	ds_SIGUNGU.Filter("SIDO_NM='" + ds_SIDO.GetColumn(this.divText.SIDO_CODE.Index, "SIDO_NM")+"'");
}

function fnId_Check(obj)
{	
	if (global.gv_WorkFlag == global.gv_INSERT)
	{
		var sArgs = "workMODE=IDCHECK scOFFI_ID="+this.divText.OFFI_ID.Text;
		var sTid = "IDCHECK";
		var sUrl = "ICCS::sys_m/Sys_mAction.do?cmd=UserMgrC";
		var sInDs = "";
		var sOutDs = "ds_IDCHK=output";
		var sCallBack = "fn_CallBack";
		
		Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
	}
}

function fn_ComboOn_Chang(obj)
{	
	if(obj.id == "SIDO_CODE"){
		ds_PT_MI_USER.SetColumn(ds_PT_MI_USER.row,"SIDO_NM",ds_SIDO.GetColumn(this.divText.SIDO_CODE.Index, "SIDO_NM"));
	}
	else if(obj.id == "SIGUNGU_CODE")
	{
		ds_PT_MI_USER.SetColumn(ds_PT_MI_USER.row,"SIGUNGU_NM",ds_SIGUNGU.GetColumn(this.divText.SIGUNGU_CODE.Index, "SIGUNGU_NM"));
	}
	
}

function ORPE_TEL_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	var sido_code = ds_PT_MI_USER.GetColumn(ds_PT_MI_USER.row, "SIDO_CODE");
	var offi_id = ds_PT_MI_USER.GetColumn(ds_PT_MI_USER.row, "OFFI_ID");
	var photo = ds_PT_MI_USER.GetColumn(ds_PT_MI_USER.row, "PHOTO");
	var sURL = global.g_ext_iccs_url + "file/User/"+ sido_code + "/" + offi_id + "/" + photo;	
	divText.Image6.ImageID = sURL;
}
